      url: "/applications",
      icon: FileText,
    },
    {
      title: "Analytics",
      url: "/analytics",
      icon: TrendingUp,
    },
    {
      title: "Messages",
      url: "/messages",
      icon: MessageSquare,
    },
    {
      title: "Favorites",
      url: "/favorites",
      icon: Heart,
    },
  ];

  const companyItems = [
    {
      title: "Dashboard",
      url: "/company",
      icon: Home,
