      title: "My Offers",
      url: "/company/offers",
      icon: TrendingUp,
    },
    {
      title: "Videos",
      url: "/company/videos",
      icon: Video,
    },
    {
      title: "Monthly Retainers",
      url: "/company/retainers",
      icon: CalendarClock,
    },
    {
      title: "Applications",
      url: "/company/applications",
      icon: FileText,
    },
    {
      title: "Creators",
      url: "/company/creators",
      icon: Users,
    },
    {
      title: "Analytics",
      url: "/company/analytics",
      icon: TrendingUp,
    },
    {
      title: "Messages",
      url: "/company/messages",
      icon: MessageSquare,
    },
    {
      title: "Reviews",
      url: "/company/reviews",
      icon: Star,
    },
  ];

  const adminItems = [
    {
      title: "Dashboard",
      url: "/admin",
      icon: Home,
    },
    {
      title: "Company Approvals",
      url: "/admin/companies",
      icon: Building2,
    },
    {
      title: "Offer Approvals",
      url: "/admin/offers",
      icon: TrendingUp,
    },
    {
      title: "Creator Management",
      url: "/admin/creators",
      icon: Users,
    },
    {
      title: "Review Management",
      url: "/admin/reviews",
      icon: Star,
    },
  ];

  const getMenuItems = () => {
    if (user?.role === 'company') return companyItems;
    if (user?.role === 'admin') return adminItems;
    return creatorItems;
  };

  const menuItems = getMenuItems();

  return (
    <Sidebar>
      <SidebarHeader className="border-b px-4 py-4">
        <Link href="/">
          <div className="flex items-center gap-2 cursor-pointer">
            <div className="h-8 w-8 rounded-md bg-primary flex items-center justify-center">
              <Zap className="h-5 w-5 text-primary-foreground" />
            </div>
            <span className="font-bold text-lg">CreatorLink</span>
          </div>
        </Link>
      </SidebarHeader>

      <SidebarContent>
        <SidebarGroup>
          <SidebarGroupLabel>
            {user?.role === 'company' ? 'Company Portal' : user?.role === 'admin' ? 'Admin Panel' : 'Creator Portal'}
          </SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              {menuItems.map((item) => (
                <SidebarMenuItem key={item.url}>
                  <SidebarMenuButton asChild isActive={location === item.url} data-testid={`nav-${item.title.toLowerCase().replace(/\s/g, '-')}`}>
                    <Link href={item.url}>
                      <item.icon className="h-4 w-4" />
                      <span>{item.title}</span>
                    </Link>
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>

        <SidebarGroup>
          <SidebarGroupLabel>Settings</SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              <SidebarMenuItem>
                <SidebarMenuButton asChild isActive={location === '/settings'} data-testid="nav-settings">
                  <Link href="/settings">
                    <Settings className="h-4 w-4" />
                    <span>Settings</span>
                  </Link>
                </SidebarMenuButton>
              </SidebarMenuItem>
              <SidebarMenuItem>
