import { useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Users, FileText, TrendingUp, DollarSign, Plus, CheckCircle } from "lucide-react";
import { Link } from "wouter";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { formatDistanceToNow } from "date-fns";

export default function CompanyDashboard() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: stats } = useQuery<any>({
    queryKey: ["/api/company/stats"],
    enabled: isAuthenticated,
  });

  const { data: applications = [], isLoading: loadingApplications } = useQuery<any[]>({
    queryKey: ["/api/company/applications"],
    enabled: isAuthenticated,
  });

  const completeApplicationMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      return await apiRequest('POST', `/api/applications/${applicationId}/complete`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/company/applications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/company/stats"] });
      toast({
        title: "Work Approved",
        description: "Creator work has been marked as complete.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to mark work as complete",
        variant: "destructive",
      });
    },
  });

  const handleMarkComplete = (applicationId: string, creatorName: string) => {
    if (confirm(`Mark work as complete for ${creatorName}? This action cannot be undone.`)) {
      completeApplicationMutation.mutate(applicationId);
    }
  };

  if (isLoading) {
    return <div className="flex items-center justify-center min-h-screen">
      <div className="animate-pulse text-lg">Loading...</div>
    </div>;
  }

  return (
    <div className="space-y-8">
      <div className="flex flex-col sm:flex-row justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold">Company Dashboard</h1>
          <p className="text-muted-foreground mt-1">Manage your offers and track creator performance</p>
        </div>
        <Link href="/company/offers/create">
          <Button className="gap-2" data-testid="button-create-offer">
            <Plus className="h-4 w-4" />
            Create New Offer
          </Button>
        </Link>
      </div>

      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-4">
        <Card className="border-card-border">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Creators</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats?.activeCreators || 0}</div>
            <p className="text-xs text-muted-foreground mt-1">
              {stats?.pendingApplications || 0} pending applications
            </p>
          </CardContent>
        </Card>

        <Card className="border-card-border">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Live Offers</CardTitle>
            <TrendingUp className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats?.liveOffers || 0}</div>
            <p className="text-xs text-muted-foreground mt-1">
              {stats?.draftOffers || 0} drafts
            </p>
          </CardContent>
        </Card>

        <Card className="border-card-border">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Applications</CardTitle>
            <FileText className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats?.totalApplications || 0}</div>
            <p className="text-xs text-muted-foreground mt-1">All time</p>
          </CardContent>
        </Card>

        <Card className="border-card-border">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Clicks</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats?.totalClicks || 0}</div>
            <p className="text-xs text-muted-foreground mt-1">
              {stats?.conversions || 0} conversions
            </p>
          </CardContent>
        </Card>
      </div>

      {stats?.companyProfile?.status === 'pending' && (
        <Card className="border-card-border bg-muted/50">
          <CardContent className="p-6">
            <div className="flex items-start gap-4">
              <div className="flex-1">
                <h3 className="font-semibold mb-2">Company Approval Pending</h3>
                <p className="text-sm text-muted-foreground">
                  Your company registration is under review. You'll be able to create offers once approved.
                </p>
              </div>
              <Badge variant="secondary">Pending</Badge>
            </div>
          </CardContent>
        </Card>
      )}

      <div className="grid md:grid-cols-2 gap-6">
        <Card className="border-card-border">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0">
            <CardTitle>Recent Applications</CardTitle>
            <Badge variant="secondary" data-testid="badge-applications-count">{applications.length}</Badge>
          </CardHeader>
          <CardContent>
            {loadingApplications ? (
              <div className="text-center py-8">
                <div className="animate-pulse text-sm text-muted-foreground">Loading applications...</div>
              </div>
            ) : applications.length === 0 ? (
              <div className="text-center py-8">
                <FileText className="h-8 w-8 text-muted-foreground/50 mx-auto mb-2" />
                <p className="text-sm text-muted-foreground">No applications yet</p>
              </div>
            ) : (
              <div className="space-y-4">
                {applications.slice(0, 5).map((app: any) => (
                  <div key={app.id} className="flex items-start justify-between gap-4 p-3 rounded-md border border-border hover-elevate" data-testid={`application-${app.id}`}>
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2 flex-wrap mb-1">
                        <h4 className="font-medium text-sm truncate" data-testid={`text-creator-${app.id}`}>{app.creatorName}</h4>
                        <Badge 
                          variant={
                            app.status === 'completed' ? 'default' : 
                            app.status === 'approved' || app.status === 'active' ? 'secondary' : 
                            'outline'
                          }
                          data-testid={`badge-status-${app.id}`}
                        >
                          {app.status}
                        </Badge>
                      </div>
                      <p className="text-xs text-muted-foreground truncate" data-testid={`text-offer-${app.id}`}>{app.offerTitle}</p>
                      <p className="text-xs text-tertiary-foreground mt-1">
                        {formatDistanceToNow(new Date(app.createdAt), { addSuffix: true })}
                      </p>
                    </div>
                    {(app.status === 'approved' || app.status === 'active') && (
                      <Button 
                        size="sm" 
                        onClick={() => handleMarkComplete(app.id, app.creatorName)}
                        disabled={completeApplicationMutation.isPending}
                        className="gap-1"
                        data-testid={`button-complete-${app.id}`}
                      >
                        <CheckCircle className="h-3 w-3" />
                        Complete
                      </Button>
                    )}
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        <Card className="border-card-border">
          <CardHeader>
            <CardTitle>Top Performing Creators</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-center py-8">
              <Users className="h-8 w-8 text-muted-foreground/50 mx-auto mb-2" />
              <p className="text-sm text-muted-foreground">No creators yet</p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/browse.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Slider } from "@/components/ui/slider";
import { Checkbox } from "@/components/ui/checkbox";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
  SheetTrigger,
} from "@/components/ui/sheet";
import { Search, SlidersHorizontal, TrendingUp, DollarSign, Clock, Star, Play, Heart } from "lucide-react";
import { Link } from "wouter";
import { apiRequest, queryClient } from "@/lib/queryClient";

const NICHES = [
  "Technology", "Fashion", "Beauty", "Fitness", "Gaming", 
  "Travel", "Food", "Lifestyle", "Business", "Education"
];

const COMMISSION_TYPES = [
  { value: "per_sale", label: "Per Sale" },
  { value: "per_lead", label: "Per Lead" },
  { value: "per_click", label: "Per Click" },
  { value: "monthly_retainer", label: "Monthly Retainer" },
  { value: "hybrid", label: "Hybrid" },
];

export default function Browse() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedNiches, setSelectedNiches] = useState<string[]>([]);
  const [commissionType, setCommissionType] = useState<string>("");
  const [commissionRange, setCommissionRange] = useState([0, 10000]);
  const [sortBy, setSortBy] = useState("newest");

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: offers, isLoading: offersLoading } = useQuery<any[]>({
    queryKey: ["/api/offers", { search: searchTerm, niches: selectedNiches, commissionType, sortBy }],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (searchTerm) params.append('search', searchTerm);
      if (selectedNiches.length > 0) params.append('niches', selectedNiches.join(','));
      if (commissionType) params.append('commissionType', commissionType);
      if (sortBy) params.append('sortBy', sortBy);

      const url = `/api/offers${params.toString() ? '?' + params.toString() : ''}`;
      const res = await fetch(url, { credentials: 'include' });
      if (!res.ok) throw new Error('Failed to fetch offers');
      return res.json();
    },
    enabled: isAuthenticated,
  });

  const toggleNiche = (niche: string) => {
    setSelectedNiches(prev =>
      prev.includes(niche) ? prev.filter(n => n !== niche) : [...prev, niche]
    );
  };

  const clearFilters = () => {
    setSelectedNiches([]);
    setCommissionType("");
    setCommissionRange([0, 10000]);
    setSearchTerm("");
  };

  const { data: favorites = [] } = useQuery<any[]>({
    queryKey: ["/api/favorites"],
    enabled: isAuthenticated,
  });

  const favoriteMutation = useMutation({
    mutationFn: async ({ offerId, isFav }: { offerId: string; isFav: boolean }) => {
      if (isFav) {
        await apiRequest("DELETE", `/api/favorites/${offerId}`);
      } else {
        await apiRequest("POST", "/api/favorites", { offerId });
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/favorites"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: "Failed to update favorites",
        variant: "destructive",
      });
    },
  });

  const handleFavoriteToggle = (e: React.MouseEvent, offerId: string) => {
    e.preventDefault();
    e.stopPropagation();
    const isFav = favorites.some(f => f.offerId === offerId);
    favoriteMutation.mutate({ offerId, isFav });
  };

  if (isLoading) {
    return <div className="flex items-center justify-center min-h-screen">
      <div className="animate-pulse text-lg">Loading...</div>
    </div>;
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold">Browse Offers</h1>
        <p className="text-muted-foreground mt-1">Discover exclusive affiliate opportunities from verified brands</p>
      </div>

      {/* Search and Filters */}
      <div className="flex flex-col sm:flex-row gap-4">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search offers..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10"
            data-testid="input-search"
          />
        </div>

        <Select value={sortBy} onValueChange={setSortBy}>
          <SelectTrigger className="w-full sm:w-48" data-testid="select-sort">
            <SelectValue placeholder="Sort by" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="newest">Newest First</SelectItem>
            <SelectItem value="highest_commission">Highest Commission</SelectItem>
            <SelectItem value="most_popular">Most Popular</SelectItem>
            <SelectItem value="trending">Trending</SelectItem>
          </SelectContent>
        </Select>

        <Sheet>
          <SheetTrigger asChild>
            <Button variant="outline" data-testid="button-filters" className="gap-2">
              <SlidersHorizontal className="h-4 w-4" />
              Filters
              {(selectedNiches.length > 0 || commissionType) && (
                <Badge variant="secondary" className="ml-1">
                  {selectedNiches.length + (commissionType ? 1 : 0)}
                </Badge>
              )}
            </Button>
          </SheetTrigger>
          <SheetContent>
            <SheetHeader>
              <SheetTitle>Filter Offers</SheetTitle>
              <SheetDescription>Refine your search with advanced filters</SheetDescription>
            </SheetHeader>

            <div className="space-y-6 mt-6">
              {/* Niche Filter */}
              <div className="space-y-3">
                <Label>Niche/Category</Label>
                <div className="space-y-2">
                  {NICHES.map((niche) => (
                    <div key={niche} className="flex items-center gap-2">
                      <Checkbox
                        id={`niche-${niche}`}
                        checked={selectedNiches.includes(niche)}
                        onCheckedChange={() => toggleNiche(niche)}
                        data-testid={`checkbox-niche-${niche}`}
                      />
                      <Label
                        htmlFor={`niche-${niche}`}
                        className="text-sm font-normal cursor-pointer"
                      >
                        {niche}
                      </Label>
                    </div>
                  ))}
                </div>
              </div>

              {/* Commission Type */}
              <div className="space-y-3">
                <Label>Commission Type</Label>
                <Select value={commissionType} onValueChange={setCommissionType}>
                  <SelectTrigger data-testid="select-commission-type">
                    <SelectValue placeholder="All types" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="">All types</SelectItem>
                    {COMMISSION_TYPES.map((type) => (
                      <SelectItem key={type.value} value={type.value}>
                        {type.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Commission Range */}
              <div className="space-y-3">
                <Label>Commission Range</Label>
                <div className="px-2 py-4">
                  <Slider
                    value={commissionRange}
                    onValueChange={setCommissionRange}
                    max={10000}
                    step={100}
                    data-testid="slider-commission"
                  />
                </div>
                <div className="flex items-center justify-between text-sm text-muted-foreground">
                  <span>${commissionRange[0]}</span>
                  <span>${commissionRange[1]}</span>
                </div>
              </div>

              <div className="pt-4 flex gap-3">
                <Button onClick={clearFilters} variant="outline" className="flex-1" data-testid="button-clear-filters">
                  Clear All
                </Button>
              </div>
            </div>
          </SheetContent>
        </Sheet>
      </div>

      {/* Offers Grid */}
      {offersLoading ? (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {[1, 2, 3, 4, 5, 6, 7, 8].map((i) => (
            <Card key={i} className="animate-pulse">
              <div className="aspect-video bg-muted rounded-t-lg" />
              <CardContent className="p-4 space-y-3">
                <div className="h-4 bg-muted rounded" />
                <div className="h-3 bg-muted rounded w-2/3" />
              </CardContent>
            </Card>
          ))}
        </div>
      ) : !offers || offers.length === 0 ? (
        <Card className="border-card-border">
          <CardContent className="p-12 text-center">
            <TrendingUp className="h-12 w-12 text-muted-foreground/50 mx-auto mb-4" />
            <h3 className="font-semibold text-lg mb-2">No offers found</h3>
            <p className="text-muted-foreground">Try adjusting your filters or search terms</p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {offers.map((offer) => {
            const isFavorite = favorites.some(f => f.offerId === offer.id);
            return (
              <Link key={offer.id} href={`/offers/${offer.id}`}>
                <Card className="hover-elevate cursor-pointer border-card-border h-full" data-testid={`card-offer-${offer.id}`}>
                  <div className="aspect-video relative bg-muted rounded-t-lg overflow-hidden">
                    {offer.featuredImageUrl ? (
                      <img src={offer.featuredImageUrl} alt={offer.title} className="w-full h-full object-cover" />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center">
                        <Play className="h-12 w-12 text-muted-foreground/50" />
                      </div>
                    )}
                    {offer.isPriority && (
                      <Badge className="absolute top-2 right-2 bg-primary">
                        Featured
                      </Badge>
                    )}
                    <button
                      className="absolute top-2 left-2 h-8 w-8 rounded-full bg-background/80 backdrop-blur flex items-center justify-center hover-elevate"
                      onClick={(e) => handleFavoriteToggle(e, offer.id)}
                      data-testid={`button-favorite-${offer.id}`}
                    >
                      <Heart className={`h-4 w-4 ${isFavorite ? 'fill-primary text-primary' : ''}`} />
                    </button>
                  </div>

                <CardContent className="p-4 space-y-3">
                  <div className="flex items-start justify-between gap-2">
                    <h3 className="font-semibold line-clamp-1 flex-1">{offer.title}</h3>
                    {offer.company?.logoUrl && (
                      <img src={offer.company.logoUrl} alt={offer.company.tradeName} className="h-8 w-8 rounded-full object-cover" />
                    )}
                  </div>

                  <p className="text-sm text-muted-foreground line-clamp-2">{offer.shortDescription}</p>

                  <div className="flex flex-wrap gap-1">
                    <Badge variant="secondary" className="text-xs">{offer.primaryNiche}</Badge>
                  </div>

                  <div className="flex items-center justify-between pt-2 border-t">
                    <div className="flex items-center gap-1 text-xs text-muted-foreground">
                      <Star className="h-3 w-3 fill-primary text-primary" />
                      <span>{offer.company?.averageRating?.toFixed(1) || '5.0'}</span>
                    </div>
                    <div className="font-mono font-semibold text-primary">
                      ${offer.commissionAmount || offer.commissionPercentage + '%'}
                    </div>
                  </div>
                </CardContent>
              </Card>
            </Link>
            );
          })}
        </div>
      )}
    </div>
  );
}

client/src/pages/offer-detail.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Heart, Star, Play, CheckCircle2, DollarSign, Clock, MapPin, Users } from "lucide-react";

export default function OfferDetail() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [, params] = useRoute("/offers/:id");
  const offerId = params?.id;

  const [showApplyDialog, setShowApplyDialog] = useState(false);
  const [applicationMessage, setApplicationMessage] = useState("");
  const [preferredCommission, setPreferredCommission] = useState("");
  const [termsAccepted, setTermsAccepted] = useState(false);
  const [selectedVideo, setSelectedVideo] = useState<any>(null);

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: offer, isLoading: offerLoading } = useQuery<any>({
    queryKey: ["/api/offers", offerId],
    enabled: !!offerId && isAuthenticated,
  });

  const { data: isFavorite } = useQuery<boolean>({
    queryKey: ["/api/favorites", offerId],
    enabled: !!offerId && isAuthenticated,
  });

  const favoriteMutation = useMutation({
    mutationFn: async () => {
      if (isFavorite) {
        await apiRequest("DELETE", `/api/favorites/${offerId}`);
      } else {
        await apiRequest("POST", "/api/favorites", { offerId });
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/favorites", offerId] });
      toast({
        title: isFavorite ? "Removed from favorites" : "Added to favorites",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update favorites",
        variant: "destructive",
      });
    },
  });

  const applyMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("POST", "/api/applications", {
        offerId,
        message: applicationMessage,
        preferredCommission,
      });
    },
    onSuccess: () => {
      setShowApplyDialog(false);
      toast({
        title: "Application Submitted!",
        description: "You'll hear back within 4 hours. Check My Applications for updates.",
      });
      setApplicationMessage("");
      setPreferredCommission("");
      setTermsAccepted(false);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to submit application",
        variant: "destructive",
      });
    },
  });

  if (isLoading || offerLoading) {
    return <div className="flex items-center justify-center min-h-screen">
      <div className="animate-pulse text-lg">Loading...</div>
    </div>;
  }

  if (!offer) {
    return <div className="text-center py-12">
      <p className="text-muted-foreground">Offer not found</p>
    </div>;
  }

  return (
    <div className="space-y-6 pb-20">
      {/* Hero Image */}
      <div className="aspect-[21/9] relative bg-muted rounded-lg overflow-hidden">
        {offer.featuredImageUrl ? (
          <img src={offer.featuredImageUrl} alt={offer.title} className="w-full h-full object-cover" />
        ) : (
          <div className="w-full h-full flex items-center justify-center">
            <Play className="h-20 w-20 text-muted-foreground/50" />
          </div>
        )}
        {offer.isPriority && (
          <Badge className="absolute top-4 right-4 bg-primary text-lg px-4 py-2">
            Featured
          </Badge>
        )}
      </div>

      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between gap-4">
        <div className="space-y-2">
          <div className="flex items-center gap-3">
            {offer.company?.logoUrl && (
              <Avatar className="h-12 w-12">
                <AvatarImage src={offer.company.logoUrl} alt={offer.company.tradeName} />
                <AvatarFallback>{offer.company.tradeName?.[0]}</AvatarFallback>
              </Avatar>
            )}
            <div>
              <h1 className="text-3xl font-bold">{offer.title}</h1>
              <p className="text-muted-foreground">{offer.company?.tradeName}</p>
            </div>
          </div>
        </div>

        <div className="flex gap-2">
          <Button
            variant="outline"
            size="icon"
            onClick={() => favoriteMutation.mutate()}
            data-testid="button-favorite"
            className="h-10 w-10"
          >
            <Heart className={`h-5 w-5 ${isFavorite ? 'fill-primary text-primary' : ''}`} />
          </Button>
        </div>
      </div>

      {/* Tabs */}
      <Tabs defaultValue="overview" className="space-y-6">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview" data-testid="tab-overview">Overview</TabsTrigger>
          <TabsTrigger value="videos" data-testid="tab-videos">Videos ({offer.videos?.length || 0})</TabsTrigger>
          <TabsTrigger value="requirements" data-testid="tab-requirements">Requirements</TabsTrigger>
          <TabsTrigger value="reviews" data-testid="tab-reviews">Reviews</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-6">
          <Card className="border-card-border">
            <CardHeader>
              <CardTitle>About This Offer</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-muted-foreground">{offer.fullDescription}</p>

              <div className="grid sm:grid-cols-2 gap-4 pt-4 border-t">
                <div className="space-y-1">
                  <div className="text-sm text-muted-foreground">Commission</div>
                  <div className="text-2xl font-bold font-mono text-primary">
                    ${offer.commissionAmount || offer.commissionPercentage + '%'}
                  </div>
                </div>
                <div className="space-y-1">
                  <div className="text-sm text-muted-foreground">Commission Type</div>
                  <Badge>{offer.commissionType.replace('_', ' ')}</Badge>
                </div>
                {offer.paymentSchedule && (
                  <div className="space-y-1">
                    <div className="text-sm text-muted-foreground">Payment Schedule</div>
                    <div className="font-semibold">{offer.paymentSchedule}</div>
                  </div>
                )}
                {offer.minimumPayout && (
                  <div className="space-y-1">
                    <div className="text-sm text-muted-foreground">Minimum Payout</div>
                    <div className="font-semibold font-mono">${offer.minimumPayout}</div>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="videos" className="space-y-6">
          {!offer.videos || offer.videos.length === 0 ? (
            <Card className="border-card-border">
              <CardContent className="p-12 text-center">
                <Play className="h-12 w-12 text-muted-foreground/50 mx-auto mb-4" />
                <p className="text-muted-foreground">No example videos available</p>
              </CardContent>
            </Card>
          ) : (
            <div className="grid sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {offer.videos.map((video: any) => (
                <Card
                  key={video.id}
                  className="hover-elevate cursor-pointer border-card-border"
                  onClick={() => setSelectedVideo(video)}
                  data-testid={`video-${video.id}`}
                >
                  <div className="aspect-video relative bg-muted rounded-t-lg overflow-hidden">
                    {video.thumbnailUrl ? (
                      <img src={video.thumbnailUrl} alt={video.title} className="w-full h-full object-cover" />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center">
                        <Play className="h-8 w-8 text-muted-foreground/50" />
                      </div>
                    )}
                    <div className="absolute inset-0 bg-black/20 flex items-center justify-center">
                      <Play className="h-12 w-12 text-white" />
                    </div>
                  </div>
                  <CardContent className="p-3">
                    <h4 className="font-semibold text-sm line-clamp-1">{video.title}</h4>
                    {video.creatorCredit && (
                      <p className="text-xs text-muted-foreground mt-1">by {video.creatorCredit}</p>
                    )}
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="requirements" className="space-y-6">
          <Card className="border-card-border">
            <CardHeader>
              <CardTitle>Creator Requirements</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {offer.minimumFollowers && (
                <div className="flex items-start gap-3">
                  <Users className="h-5 w-5 text-muted-foreground mt-0.5" />
                  <div>
                    <div className="font-semibold">Minimum Followers</div>
                    <div className="text-muted-foreground">{offer.minimumFollowers.toLocaleString()}</div>
                  </div>
                </div>
              )}
              {offer.allowedPlatforms && offer.allowedPlatforms.length > 0 && (
                <div className="flex items-start gap-3">
                  <CheckCircle2 className="h-5 w-5 text-muted-foreground mt-0.5" />
                  <div>
                    <div className="font-semibold">Allowed Platforms</div>
                    <div className="flex gap-2 mt-1 flex-wrap">
                      {offer.allowedPlatforms.map((platform: string) => (
                        <Badge key={platform} variant="secondary">{platform}</Badge>
                      ))}
                    </div>
                  </div>
                </div>
              )}
              {offer.geographicRestrictions && offer.geographicRestrictions.length > 0 && (
                <div className="flex items-start gap-3">
                  <MapPin className="h-5 w-5 text-muted-foreground mt-0.5" />
                  <div>
                    <div className="font-semibold">Geographic Restrictions</div>
                    <div className="text-muted-foreground">{offer.geographicRestrictions.join(', ')}</div>
                  </div>
                </div>
              )}
              {offer.contentStyleRequirements && (
                <div className="pt-4 border-t">
                  <div className="font-semibold mb-2">Content Style Requirements</div>
                  <p className="text-muted-foreground">{offer.contentStyleRequirements}</p>
                </div>
              )}
              {offer.brandSafetyRequirements && (
                <div className="pt-4 border-t">
                  <div className="font-semibold mb-2">Brand Safety Requirements</div>
                  <p className="text-muted-foreground">{offer.brandSafetyRequirements}</p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="reviews" className="space-y-6">
          <Card className="border-card-border">
            <CardHeader>
              <CardTitle>Creator Reviews</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-center py-12">
                <Star className="h-12 w-12 text-muted-foreground/50 mx-auto mb-4" />
                <p className="text-muted-foreground">No reviews yet</p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {/* Sticky Apply Button */}
      <div className="fixed bottom-0 left-0 right-0 border-t bg-background/95 backdrop-blur p-4 z-40">
        <div className="max-w-7xl mx-auto flex justify-end">
          <Dialog open={showApplyDialog} onOpenChange={setShowApplyDialog}>
            <DialogTrigger asChild>
              <Button size="lg" className="gap-2" data-testid="button-apply">
                Apply Now
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Apply to {offer.title}</DialogTitle>
                <DialogDescription>
                  Tell the company why you're interested in promoting their offer
                </DialogDescription>
              </DialogHeader>

              <div className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="message">Why are you interested? *</Label>
                  <Textarea
                    id="message"
                    placeholder="Tell the company about your audience and why you'd be a great fit..."
                    value={applicationMessage}
                    onChange={(e) => setApplicationMessage(e.target.value.slice(0, 500))}
                    className="min-h-32"
                    data-testid="textarea-application-message"
                  />
                  <p className="text-xs text-muted-foreground text-right">
                    {applicationMessage.length}/500
                  </p>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="commission">Preferred Commission Model</Label>
                  <Select value={preferredCommission} onValueChange={setPreferredCommission}>
                    <SelectTrigger id="commission" data-testid="select-preferred-commission">
                      <SelectValue placeholder="Select preferred model" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="default">Standard Commission</SelectItem>
                      {offer.commissionType === 'hybrid' && (
                        <>
                          <SelectItem value="per_sale">Per Sale</SelectItem>
                          <SelectItem value="retainer">Monthly Retainer</SelectItem>
                        </>
                      )}
                    </SelectContent>
                  </Select>
                </div>

                <div className="flex items-center gap-2 pt-4">
                  <Checkbox
                    id="terms"
                    checked={termsAccepted}
                    onCheckedChange={(checked) => setTermsAccepted(checked as boolean)}
                    data-testid="checkbox-terms"
                  />
                  <Label htmlFor="terms" className="text-sm font-normal cursor-pointer">
                    I accept the terms and conditions and agree to promote this offer ethically
                  </Label>
                </div>
              </div>

              <DialogFooter>
                <Button
                  onClick={() => applyMutation.mutate()}
                  disabled={!applicationMessage || !termsAccepted || applyMutation.isPending}
                  data-testid="button-submit-application"
                >
                  {applyMutation.isPending ? "Submitting..." : "Submit Application"}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Video Player Dialog */}
      {selectedVideo && (
        <Dialog open={!!selectedVideo} onOpenChange={() => setSelectedVideo(null)}>
          <DialogContent className="max-w-4xl">
            <DialogHeader>
              <DialogTitle>{selectedVideo.title}</DialogTitle>
              {selectedVideo.description && (
                <DialogDescription>{selectedVideo.description}</DialogDescription>
              )}
            </DialogHeader>
            <div className="aspect-video bg-muted rounded-lg overflow-hidden">
              {selectedVideo.videoUrl && (
                <video src={selectedVideo.videoUrl} controls className="w-full h-full" />
              )}
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

client/src/components/ui/badge.tsx.content:
import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  // Whitespace-nowrap: Badges should never wrap.
  "whitespace-nowrap inline-flex items-center rounded-md border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2" +
  " hover-elevate " ,
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground shadow-xs",
        secondary: "border-transparent bg-secondary text-secondary-foreground",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground shadow-xs",

        outline: " border [border-color:var(--badge-outline)] shadow-xs",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  );
}

export { Badge, badgeVariants }

