import { useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Users, MessageSquare, TrendingUp, ExternalLink } from "lucide-react";
import { Link } from "wouter";

export default function CompanyCreators() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: applications = [], isLoading: loadingCreators } = useQuery<any[]>({
    queryKey: ["/api/company/applications"],
    enabled: isAuthenticated,
  });

  // Get unique creators from approved applications
  const creators = applications
    .filter((app: any) => app.status === 'approved' && app.creator)
    .reduce((acc: any[], app: any) => {
      const existing = acc.find(c => c.id === app.creator.id);
      if (!existing) {
        acc.push({
          ...app.creator,
          applications: [app],
          totalClicks: app.clickCount || 0,
          totalConversions: app.conversionCount || 0,
          totalEarnings: parseFloat(app.totalEarnings || '0'),
        });
      } else {
        existing.applications.push(app);
        existing.totalClicks += app.clickCount || 0;
        existing.totalConversions += app.conversionCount || 0;
        existing.totalEarnings += parseFloat(app.totalEarnings || '0');
      }
      return acc;
    }, []);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-bold">Creators</h1>
        <p className="text-muted-foreground mt-1">
          Manage relationships with creators promoting your offers
        </p>
      </div>

      {loadingCreators ? (
        <div className="text-center py-12">
          <div className="animate-pulse text-lg text-muted-foreground">
            Loading creators...
          </div>
        </div>
      ) : creators.length === 0 ? (
        <Card className="border-card-border">
          <CardContent className="flex flex-col items-center justify-center py-12">
            <Users className="h-12 w-12 text-muted-foreground/50 mb-4" />
            <h3 className="text-lg font-semibold mb-2">No active creators yet</h3>
            <p className="text-sm text-muted-foreground text-center max-w-md">
              Approved creators will appear here when they start promoting your offers
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {creators.map((creator: any) => (
            <Card key={creator.id} className="border-card-border" data-testid={`card-creator-${creator.id}`}>
              <CardHeader>
                <div className="flex items-start gap-3">
                  <Avatar className="h-12 w-12">
                    <AvatarImage src={creator.profileImageUrl} />
                    <AvatarFallback>
                      {creator.firstName?.[0] || 'C'}
                    </AvatarFallback>
                  </Avatar>
                  <div className="flex-1 min-w-0">
                    <CardTitle className="text-lg line-clamp-1">
                      {creator.firstName || 'Creator'}
                    </CardTitle>
                    <p className="text-sm text-muted-foreground">
                      {creator.applications.length} active {creator.applications.length === 1 ? 'offer' : 'offers'}
                    </p>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                {creator.bio && (
                  <p className="text-sm text-muted-foreground line-clamp-2">
                    {creator.bio}
                  </p>
                )}

                <div className="grid grid-cols-3 gap-3 pt-3 border-t">
                  <div className="text-center">
                    <div className="text-xs text-muted-foreground mb-1">Clicks</div>
                    <div className="text-sm font-semibold">{creator.totalClicks}</div>
                  </div>
                  <div className="text-center">
                    <div className="text-xs text-muted-foreground mb-1">Conversions</div>
                    <div className="text-sm font-semibold">{creator.totalConversions}</div>
                  </div>
                  <div className="text-center">
                    <div className="text-xs text-muted-foreground mb-1">Earned</div>
                    <div className="text-sm font-semibold">${creator.totalEarnings.toFixed(2)}</div>
                  </div>
                </div>

                {(creator.youtubeUrl || creator.tiktokUrl || creator.instagramUrl) && (
                  <div className="flex flex-wrap gap-2 pt-2">
                    {creator.youtubeUrl && (
                      <Badge variant="outline" className="gap-1">
                        <ExternalLink className="h-3 w-3" />
                        YouTube
                      </Badge>
                    )}
                    {creator.tiktokUrl && (
                      <Badge variant="outline" className="gap-1">
                        <ExternalLink className="h-3 w-3" />
                        TikTok
                      </Badge>
                    )}
                    {creator.instagramUrl && (
                      <Badge variant="outline" className="gap-1">
                        <ExternalLink className="h-3 w-3" />
                        Instagram
                      </Badge>
                    )}
                  </div>
                )}

                <div className="flex gap-2 pt-2">
                  <Link href="/company/messages" className="flex-1">
                    <Button 
                      variant="outline" 
                      className="w-full gap-2"
                      data-testid={`button-message-${creator.id}`}
                    >
                      <MessageSquare className="h-4 w-4" />
                      Message
                    </Button>
                  </Link>
                  <Link href="/company/analytics" className="flex-1">
                    <Button 
                      variant="outline" 
                      className="w-full gap-2"
                      data-testid={`button-analytics-${creator.id}`}
                    >
                      <TrendingUp className="h-4 w-4" />
                      Analytics
                    </Button>
                  </Link>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/pages/company-applications.ts.content:
import { useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { FileText, CheckCircle, Clock, XCircle, MessageCircle } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { formatDistanceToNow } from "date-fns";

export default function CompanyApplications() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [, setLocation] = useLocation();

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: applications = [], isLoading: loadingApplications } = useQuery<any[]>({
    queryKey: ["/api/company/applications"],
    enabled: isAuthenticated,
  });

  const completeApplicationMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      const response = await apiRequest('POST', `/api/applications/${applicationId}/complete`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/company/applications"] });
      toast({
        title: "Work Approved",
        description: "Creator work has been marked as complete.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to mark work as complete",
        variant: "destructive",
      });
    },
  });

  const startConversationMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      const response = await apiRequest('POST', '/api/conversations/start', { applicationId });
      return response.json();
    },
    onSuccess: (data: any) => {
      // Redirect to company messages with conversation selected
      setLocation(`/company/messages?conversation=${data.conversationId}`);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to start conversation",
        variant: "destructive",
      });
    },
  });

  const approveApplicationMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      const response = await apiRequest('PUT', `/api/applications/${applicationId}/approve`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/company/applications"] });
      toast({
        title: "Application Approved",
        description: "Creator has been approved and tracking link generated.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to approve application",
        variant: "destructive",
      });
    },
  });

  const rejectApplicationMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      const response = await apiRequest('PUT', `/api/applications/${applicationId}/reject`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/company/applications"] });
      toast({
        title: "Application Rejected",
        description: "Application has been rejected.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reject application",
        variant: "destructive",
      });
    },
  });

  const handleMarkComplete = (applicationId: string, creatorName: string) => {
    if (confirm(`Mark work as complete for ${creatorName}? This action cannot be undone.`)) {
      completeApplicationMutation.mutate(applicationId);
    }
  };

  const handleApprove = (applicationId: string) => {
    if (confirm("Approve this application? This will generate a tracking link for the creator.")) {
      approveApplicationMutation.mutate(applicationId);
    }
  };

  const handleReject = (applicationId: string) => {
    if (confirm("Reject this application? This action cannot be undone.")) {
      rejectApplicationMutation.mutate(applicationId);
    }
  };

  const handleMessageCreator = (applicationId: string) => {
    startConversationMutation.mutate(applicationId);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'approved':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'pending':
        return <Clock className="h-4 w-4 text-yellow-500" />;
      case 'rejected':
        return <XCircle className="h-4 w-4 text-red-500" />;
      default:
        return <FileText className="h-4 w-4" />;
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-bold">Applications</h1>
        <p className="text-muted-foreground mt-1">
          Review and manage creator applications for your offers
        </p>
      </div>

      {loadingApplications ? (
        <div className="text-center py-12">
          <div className="animate-pulse text-lg text-muted-foreground">
            Loading applications...
          </div>
        </div>
      ) : applications.length === 0 ? (
        <Card className="border-card-border">
          <CardContent className="flex flex-col items-center justify-center py-12">
            <FileText className="h-12 w-12 text-muted-foreground/50 mb-4" />
            <h3 className="text-lg font-semibold mb-2">No applications yet</h3>
            <p className="text-sm text-muted-foreground text-center max-w-md">
              Creators will appear here when they apply to your offers
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-4">
          {applications.map((app: any) => (
            <Card key={app.id} className="border-card-border" data-testid={`card-application-${app.id}`}>
              <CardHeader>
                <div className="flex items-start justify-between gap-4">
                  <div className="flex items-center gap-3">
                    <Avatar className="h-10 w-10">
                      <AvatarImage src={app.creator?.profileImageUrl} />
                      <AvatarFallback>
                        {app.creator?.firstName?.[0] || 'C'}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <CardTitle className="text-lg">
                        {app.creator?.firstName || 'Creator'}
                      </CardTitle>
                      <p className="text-sm text-muted-foreground">
                        {app.offer?.title || 'Offer'}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    {getStatusIcon(app.status)}
                    <Badge 
                      variant={
                        app.status === 'approved' ? 'default' : 
                        app.status === 'pending' ? 'secondary' : 
                        'destructive'
                      }
                      data-testid={`badge-status-${app.id}`}
                    >
                      {app.status}
                    </Badge>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                  <div>
                    <div className="text-muted-foreground mb-1">Applied</div>
                    <div className="font-medium">
                      {formatDistanceToNow(new Date(app.createdAt), { addSuffix: true })}
                    </div>
                  </div>
                  <div>
                    <div className="text-muted-foreground mb-1">Total Clicks</div>
                    <div className="font-medium">{app.clickCount || 0}</div>
                  </div>
                  <div>
                    <div className="text-muted-foreground mb-1">Conversions</div>
                    <div className="font-medium">{app.conversionCount || 0}</div>
                  </div>
                  <div>
                    <div className="text-muted-foreground mb-1">Earnings</div>
                    <div className="font-medium">${app.totalEarnings || '0.00'}</div>
                  </div>
                </div>

                {app.trackingLink && (
                  <div className="p-3 bg-muted/50 rounded-md">
                    <div className="text-xs text-muted-foreground mb-1">Tracking Link</div>
                    <code className="text-xs break-all">{app.trackingLink}</code>
                  </div>
                )}

                {/* Approve/Reject buttons for pending applications */}
                {app.status === 'pending' && (
                  <div className="flex gap-2">
                    <Button
                      onClick={() => handleApprove(app.id)}
                      variant="default"
                      className="flex-1"
                      disabled={approveApplicationMutation.isPending}
                      data-testid={`button-approve-${app.id}`}
                    >
                      <CheckCircle className="h-4 w-4 mr-2" />
                      {approveApplicationMutation.isPending ? 'Approving...' : 'Approve'}
                    </Button>
                    <Button
                      onClick={() => handleReject(app.id)}
                      variant="destructive"
                      className="flex-1"
                      disabled={rejectApplicationMutation.isPending}
                      data-testid={`button-reject-${app.id}`}
                    >
                      <XCircle className="h-4 w-4 mr-2" />
                      {rejectApplicationMutation.isPending ? 'Rejecting...' : 'Reject'}
                    </Button>
                  </div>
                )}

                {/* Message and Complete buttons for approved applications */}
                {(app.status === 'approved' || app.status === 'rejected') && (
                  <div className="flex gap-2">
                    <Button
                      onClick={() => handleMessageCreator(app.id)}
                      variant="outline"
                      className="flex-1"
                      disabled={startConversationMutation.isPending}
                      data-testid={`button-message-creator-${app.id}`}
                    >
                      <MessageCircle className="h-4 w-4 mr-2" />
                      {startConversationMutation.isPending ? 'Opening...' : 'Message Creator'}
                    </Button>

                    {app.status === 'approved' && !app.completedAt && (
                      <Button
                        onClick={() => handleMarkComplete(app.id, app.creator?.firstName || 'this creator')}
                        variant="default"
                        className="flex-1"
                        disabled={completeApplicationMutation.isPending}
                        data-testid={`button-mark-complete-${app.id}`}
                      >
                        <CheckCircle className="h-4 w-4 mr-2" />
                        {completeApplicationMutation.isPending ? 'Processing...' : 'Mark Work Complete'}
                      </Button>
                    )}
                  </div>
                )}

                {app.completedAt && (
                  <div className="flex items-center gap-2 text-sm text-green-600 dark:text-green-400">
                    <CheckCircle className="h-4 w-4" />
                    <span>
                      Work completed {formatDistanceToNow(new Date(app.completedAt), { addSuffix: true })}
                    </span>
                  </div>
                )}
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/pages/company-reviews.tsx.content:
import { useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Star, MessageSquare } from "lucide-react";
import { formatDistanceToNow } from "date-fns";

export default function CompanyReviews() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: offers = [] } = useQuery<any[]>({
    queryKey: ["/api/offers"],
    enabled: isAuthenticated,
  });

  const { data: reviews = [], isLoading: loadingReviews } = useQuery<any[]>({
    queryKey: ["/api/reviews"],
    enabled: isAuthenticated,
  });

  // Filter reviews for this company's offers
  const companyReviews = reviews.filter((review: any) => 
    offers.some((offer: any) => offer.id === review.offerId)
  );

  const renderStars = (rating: number) => {
    return (
      <div className="flex gap-0.5">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`h-4 w-4 ${
              star <= rating
                ? 'fill-yellow-400 text-yellow-400'
                : 'text-muted-foreground/30'
            }`}
          />
        ))}
      </div>
    );
  };

  const averageRating = companyReviews.length > 0
    ? (companyReviews.reduce((sum: number, r: any) => sum + r.rating, 0) / companyReviews.length).toFixed(1)
    : '0.0';

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-bold">Reviews</h1>
        <p className="text-muted-foreground mt-1">
          See what creators are saying about your offers
        </p>
      </div>

      {companyReviews.length > 0 && (
        <div className="grid gap-6 md:grid-cols-3">
          <Card className="border-card-border">
            <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Average Rating</CardTitle>
              <Star className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{averageRating}</div>
              <div className="flex gap-1 mt-2">
                {renderStars(Math.round(parseFloat(averageRating)))}
              </div>
            </CardContent>
          </Card>

          <Card className="border-card-border">
            <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Reviews</CardTitle>
              <MessageSquare className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{companyReviews.length}</div>
              <p className="text-xs text-muted-foreground mt-1">
                Across {offers.length} {offers.length === 1 ? 'offer' : 'offers'}
              </p>
            </CardContent>
          </Card>

          <Card className="border-card-border">
            <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">5-Star Reviews</CardTitle>
              <Star className="h-4 w-4 text-yellow-400" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {companyReviews.filter((r: any) => r.rating === 5).length}
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                {companyReviews.length > 0
                  ? `${Math.round((companyReviews.filter((r: any) => r.rating === 5).length / companyReviews.length) * 100)}%`
                  : '0%'} of total
              </p>
            </CardContent>
          </Card>
        </div>
      )}

      {loadingReviews ? (
        <div className="text-center py-12">
          <div className="animate-pulse text-lg text-muted-foreground">
            Loading reviews...
          </div>
        </div>
      ) : companyReviews.length === 0 ? (
        <Card className="border-card-border">
          <CardContent className="flex flex-col items-center justify-center py-12">
            <Star className="h-12 w-12 text-muted-foreground/50 mb-4" />
            <h3 className="text-lg font-semibold mb-2">No reviews yet</h3>
            <p className="text-sm text-muted-foreground text-center max-w-md">
              Reviews from creators will appear here once they complete campaigns
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-4">
          {companyReviews.map((review: any) => (
            <Card key={review.id} className="border-card-border" data-testid={`card-review-${review.id}`}>
              <CardHeader>
                <div className="flex items-start justify-between gap-4">
                  <div className="flex items-center gap-3">
                    <Avatar className="h-10 w-10">
                      <AvatarImage src={review.creator?.profileImageUrl} />
                      <AvatarFallback>
                        {review.creator?.firstName?.[0] || 'C'}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <CardTitle className="text-base">
                        {review.creator?.firstName || 'Creator'}
                      </CardTitle>
                      <p className="text-sm text-muted-foreground">
                        {review.offer?.title || 'Offer'}
                      </p>
                    </div>
                  </div>
                  <div className="flex flex-col items-end gap-2">
                    {renderStars(review.rating)}
                    <span className="text-xs text-muted-foreground">
                      {formatDistanceToNow(new Date(review.createdAt), { addSuffix: true })}
                    </span>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <p className="text-sm">{review.comment}</p>

                {review.response && (
                  <div className="mt-4 p-3 bg-muted/50 rounded-md">
                    <div className="text-xs font-semibold text-muted-foreground mb-1">
                      Your Response
                    </div>
                    <p className="text-sm">{review.response}</p>
                  </div>
                )}
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/pages/company-offer-create.ts.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft } from "lucide-react";
import { Link } from "wouter";

export default function CompanyOfferCreate() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [, setLocation] = useLocation();

  const [formData, setFormData] = useState({
    title: "",
    productName: "",
    shortDescription: "",
    fullDescription: "",
    primaryNiche: "",
    productUrl: "",
    commissionType: "per_sale" as const,
    commissionRate: "",
    commissionAmount: "",
    status: "draft" as const,
  });

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "Please log in to create offers",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const createOfferMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      // Build payload that exactly matches createOfferSchema
      const payload: any = {
        title: data.title,
        productName: data.productName,
        shortDescription: data.shortDescription,
        fullDescription: data.fullDescription,
        primaryNiche: data.primaryNiche,
        productUrl: data.productUrl,
        commissionType: data.commissionType,
        commissionPercentage: data.commissionType === "per_sale" && data.commissionRate ? data.commissionRate : null,
        commissionAmount: data.commissionType !== "per_sale" && data.commissionAmount ? data.commissionAmount : null,
      };

      return await apiRequest("POST", "/api/offers", payload);
    },
    onSuccess: () => {
      toast({
        title: "Offer Created",
        description: "Your offer has been created successfully",
      });
      setLocation("/company/offers");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create offer",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Validation
    if (!formData.title.trim()) {
      toast({
        title: "Validation Error",
        description: "Please enter an offer title",
        variant: "destructive",
      });
      return;
    }

    if (!formData.productName.trim()) {
      toast({
        title: "Validation Error",
        description: "Please enter a product name",
        variant: "destructive",
      });
      return;
    }

    if (!formData.shortDescription.trim()) {
      toast({
        title: "Validation Error",
        description: "Please enter a short description",
        variant: "destructive",
      });
      return;
    }

    if (!formData.fullDescription.trim()) {
      toast({
        title: "Validation Error",
        description: "Please enter a full description",
        variant: "destructive",
      });
      return;
    }

    if (!formData.primaryNiche.trim()) {
      toast({
        title: "Validation Error",
        description: "Please select a primary niche",
        variant: "destructive",
      });
      return;
    }

    if (!formData.productUrl.trim()) {
      toast({
        title: "Validation Error",
        description: "Please enter a product URL",
        variant: "destructive",
      });
      return;
    }

    // Validate URL format
    if (formData.productUrl.trim() && !formData.productUrl.match(/^https?:\/\/.+/i) && !formData.productUrl.includes('.')) {
      toast({
        title: "Validation Error",
        description: "Please enter a valid URL (e.g., https://example.com or example.com)",
        variant: "destructive",
      });
      return;
    }

    if (formData.commissionType === "per_sale" && !formData.commissionRate) {
      toast({
        title: "Validation Error",
        description: "Please enter a commission rate for per-sale offers",
        variant: "destructive",
      });
      return;
    }

    if (formData.commissionType === "per_sale" && formData.commissionRate) {
      const rate = parseFloat(formData.commissionRate);
      if (isNaN(rate) || rate <= 0 || rate > 100) {
        toast({
          title: "Validation Error",
          description: "Commission rate must be between 0 and 100",
          variant: "destructive",
        });
        return;
      }
    }

    if (formData.commissionType !== "per_sale" && !formData.commissionAmount) {
      toast({
        title: "Validation Error",
        description: "Please enter a commission amount",
        variant: "destructive",
      });
      return;
    }

    if (formData.commissionType !== "per_sale" && formData.commissionAmount) {
      const amount = parseFloat(formData.commissionAmount);
      if (isNaN(amount) || amount <= 0) {
        toast({
          title: "Validation Error",
          description: "Commission amount must be greater than 0",
          variant: "destructive",
        });
        return;
      }
    }

    createOfferMutation.mutate(formData);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto space-y-6">
      <div className="flex items-center gap-4">
        <Link href="/company/offers">
          <Button variant="ghost" size="icon" data-testid="button-back">
            <ArrowLeft className="h-4 w-4" />
          </Button>
        </Link>
        <div>
          <h1 className="text-3xl font-bold">Create New Offer</h1>
          <p className="text-muted-foreground mt-1">
            Set up an affiliate offer for creators to promote
          </p>
        </div>
      </div>

      <Card className="border-card-border">
        <CardHeader>
          <CardTitle>Offer Details</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="space-y-2">
              <Label htmlFor="title">Offer Title *</Label>
              <Input
                id="title"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                placeholder="e.g., Premium Fitness App Affiliate Program"
                maxLength={100}
                data-testid="input-title"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="productName">Product Name *</Label>
              <Input
                id="productName"
                value={formData.productName}
                onChange={(e) => setFormData({ ...formData, productName: e.target.value })}
                placeholder="e.g., FitPro Premium"
                data-testid="input-product-name"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="shortDescription">Short Description * (Max 200 characters)</Label>
              <Textarea
                id="shortDescription"
                value={formData.shortDescription}
                onChange={(e) => setFormData({ ...formData, shortDescription: e.target.value })}
                placeholder="Brief summary for search results and previews..."
                maxLength={200}
                rows={2}
                data-testid="input-short-description"
              />
              <p className="text-xs text-muted-foreground">
                {formData.shortDescription.length}/200 characters
              </p>
            </div>

            <div className="space-y-2">
              <Label htmlFor="fullDescription">Full Description *</Label>
              <Textarea
                id="fullDescription"
                value={formData.fullDescription}
                onChange={(e) => setFormData({ ...formData, fullDescription: e.target.value })}
                placeholder="Detailed description of your offer, target audience, benefits, and what makes it special..."
                rows={6}
                data-testid="input-full-description"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="primaryNiche">Primary Niche *</Label>
              <Select
                value={formData.primaryNiche}
                onValueChange={(value) => setFormData({ ...formData, primaryNiche: value })}
              >
                <SelectTrigger id="primaryNiche" data-testid="select-primary-niche">
                  <SelectValue placeholder="Select a niche" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="fitness">Fitness & Health</SelectItem>
                  <SelectItem value="tech">Technology & Software</SelectItem>
                  <SelectItem value="beauty">Beauty & Fashion</SelectItem>
                  <SelectItem value="food">Food & Cooking</SelectItem>
                  <SelectItem value="gaming">Gaming</SelectItem>
                  <SelectItem value="finance">Finance & Investing</SelectItem>
                  <SelectItem value="education">Education & Learning</SelectItem>
                  <SelectItem value="travel">Travel & Lifestyle</SelectItem>
                  <SelectItem value="home">Home & Garden</SelectItem>
                  <SelectItem value="entertainment">Entertainment</SelectItem>
                  <SelectItem value="other">Other</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="productUrl">Product URL *</Label>
              <Input
                id="productUrl"
                value={formData.productUrl}
                onChange={(e) => setFormData({ ...formData, productUrl: e.target.value })}
                placeholder="https://yourproduct.com"
                data-testid="input-product-url"
              />
            </div>

            <div className="grid gap-6 md:grid-cols-2">
              <div className="space-y-2">
                <Label htmlFor="commissionType">Commission Type *</Label>
                <Select
                  value={formData.commissionType}
                  onValueChange={(value: any) =>
                    setFormData({ ...formData, commissionType: value })
                  }
                >
                  <SelectTrigger id="commissionType" data-testid="select-commission-type">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="per_sale">Per Sale (Percentage)</SelectItem>
                    <SelectItem value="per_lead">Per Lead (Flat Rate)</SelectItem>
                    <SelectItem value="per_click">Per Click (CPC)</SelectItem>
                    <SelectItem value="monthly_retainer">Monthly Retainer</SelectItem>
                    <SelectItem value="hybrid">Hybrid</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {formData.commissionType === "per_sale" ? (
                <div className="space-y-2">
                  <Label htmlFor="commissionRate">Commission Rate (%) *</Label>
                  <Input
                    id="commissionRate"
                    type="number"
                    step="0.01"
                    min="0"
                    max="100"
                    value={formData.commissionRate}
                    onChange={(e) =>
                      setFormData({ ...formData, commissionRate: e.target.value })
                    }
                    placeholder="e.g., 10"
                    data-testid="input-commission-rate"
                  />
                </div>
              ) : (
                <div className="space-y-2">
                  <Label htmlFor="commissionAmount">Commission Amount ($) *</Label>
                  <Input
                    id="commissionAmount"
                    type="number"
                    step="0.01"
                    min="0"
                    value={formData.commissionAmount}
                    onChange={(e) =>
                      setFormData({ ...formData, commissionAmount: e.target.value })
                    }
                    placeholder="e.g., 50.00"
                    data-testid="input-commission-amount"
                  />
                </div>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="status">Status</Label>
              <Select
                value={formData.status}
                onValueChange={(value: any) => setFormData({ ...formData, status: value })}
              >
                <SelectTrigger id="status" data-testid="select-status">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="draft">Draft</SelectItem>
                  <SelectItem value="live">Live</SelectItem>
                </SelectContent>
              </Select>
              <p className="text-xs text-muted-foreground">
                Draft offers are not visible to creators
              </p>
            </div>

            <div className="flex gap-3 pt-4">
              <Button
                type="submit"
                disabled={createOfferMutation.isPending}
                data-testid="button-create-offer"
              >
                {createOfferMutation.isPending ? "Creating..." : "Create Offer"}
              </Button>
              <Link href="/company/offers">
                <Button type="button" variant="outline" data-testid="button-cancel">
                  Cancel
                </Button>
              </Link>
            </div>
          </form>
        </CardContent>
      </Card>

      <Card className="border-card-border bg-muted/50">
        <CardContent className="p-4">
          <p className="text-sm text-muted-foreground">
            <strong>Note:</strong> After creating your offer, you'll need to upload 6-12
            promotional videos before it can go live. You can do this from the offer details page.
          </p>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/admin-reviews.tsx.content:
import { useEffect, useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Star, Eye, EyeOff, Trash2, FileText, CheckCircle2, AlertCircle } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";

export default function AdminReviews() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [selectedReview, setSelectedReview] = useState<any>(null);
  const [isNoteDialogOpen, setIsNoteDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [adminNote, setAdminNote] = useState("");
  const [editedReview, setEditedReview] = useState<any>(null);

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: reviews = [], isLoading: loadingReviews } = useQuery<any[]>({
    queryKey: ["/api/admin/reviews"],
    enabled: isAuthenticated,
  });

  const hideReviewMutation = useMutation({
    mutationFn: async (reviewId: string) => {
      const response = await apiRequest("POST", `/api/admin/reviews/${reviewId}/hide`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/reviews"] });
      toast({
        title: "Success",
        description: "Review hidden successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to hide review",
        variant: "destructive",
      });
    },
  });

  const deleteReviewMutation = useMutation({
    mutationFn: async (reviewId: string) => {
      const response = await apiRequest("DELETE", `/api/admin/reviews/${reviewId}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/reviews"] });
      toast({
        title: "Success",
        description: "Review deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete review",
        variant: "destructive",
      });
    },
  });

  const saveNoteMutation = useMutation({
    mutationFn: async ({ reviewId, note }: { reviewId: string; note: string }) => {
      const response = await apiRequest("POST", `/api/admin/reviews/${reviewId}/note`, { note });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/reviews"] });
      toast({
        title: "Success",
        description: "Admin note saved successfully",
      });
      setIsNoteDialogOpen(false);
      setAdminNote("");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save note",
        variant: "destructive",
      });
    },
  });

  const updateReviewMutation = useMutation({
    mutationFn: async ({ reviewId, updates }: { reviewId: string; updates: any }) => {
      const response = await apiRequest("PATCH", `/api/admin/reviews/${reviewId}`, updates);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/reviews"] });
      toast({
        title: "Success",
        description: "Review updated successfully",
      });
      setIsEditDialogOpen(false);
      setEditedReview(null);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update review",
        variant: "destructive",
      });
    },
  });

  const approveReviewMutation = useMutation({
    mutationFn: async (reviewId: string) => {
      const response = await apiRequest("POST", `/api/admin/reviews/${reviewId}/approve`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/reviews"] });
      toast({
        title: "Success",
        description: "Review approved successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to approve review",
        variant: "destructive",
      });
    },
  });

  const handleAddNote = (review: any) => {
    setSelectedReview(review);
    setAdminNote(review.adminNote || "");
    setIsNoteDialogOpen(true);
  };

  const handleEdit = (review: any) => {
    setSelectedReview(review);
    setEditedReview({
      reviewText: review.reviewText || "",
      overallRating: review.overallRating,
      paymentSpeedRating: review.paymentSpeedRating,
      communicationRating: review.communicationRating,
      offerQualityRating: review.offerQualityRating,
      supportRating: review.supportRating,
    });
    setIsEditDialogOpen(true);
  };

  const renderStars = (rating: number) => {
    return (
      <div className="flex gap-0.5">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`h-4 w-4 ${
              star <= rating
                ? 'fill-yellow-400 text-yellow-400'
                : 'text-muted-foreground/30'
            }`}
          />
        ))}
      </div>
    );
  };

  if (isLoading || loadingReviews) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-bold">Review Management</h1>
        <p className="text-muted-foreground mt-1">
          View, approve, edit, or delete reviews across the platform
        </p>
      </div>

      <div className="grid gap-6 md:grid-cols-3">
        <Card className="border-card-border" data-testid="card-total-reviews">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Reviews</CardTitle>
            <FileText className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-total-reviews">{reviews.length}</div>
          </CardContent>
        </Card>

        <Card className="border-card-border" data-testid="card-hidden-reviews">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Hidden</CardTitle>
            <EyeOff className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-hidden-reviews">
              {reviews.filter((r: any) => r.isHidden).length}
            </div>
          </CardContent>
        </Card>

        <Card className="border-card-border" data-testid="card-pending-approval">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Pending Approval</CardTitle>
            <AlertCircle className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-primary" data-testid="text-pending-approval">
              {reviews.filter((r: any) => !r.isApproved).length}
            </div>
          </CardContent>
        </Card>
      </div>

      {reviews.length === 0 ? (
        <Card className="border-card-border">
          <CardContent className="pt-6">
            <div className="text-center py-12">
              <FileText className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
              <h3 className="text-lg font-medium mb-2">No reviews yet</h3>
              <p className="text-muted-foreground">
                Reviews will appear here once creators start submitting them
              </p>
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-4">
          {reviews.map((review: any) => (
            <Card 
              key={review.id} 
              className={`border-card-border ${review.isHidden ? 'opacity-50' : ''}`}
              data-testid={`card-review-${review.id}`}
            >
              <CardHeader>
                <div className="flex items-start justify-between gap-4">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-2">
                      {renderStars(review.overallRating)}
                      <span className="text-sm text-muted-foreground">
                        {new Date(review.createdAt).toLocaleDateString()}
                      </span>
                      {review.isHidden && (
                        <Badge variant="secondary" data-testid={`badge-hidden-${review.id}`}>
                          <EyeOff className="h-3 w-3 mr-1" />
                          Hidden
                        </Badge>
                      )}
                      {!review.isApproved && (
                        <Badge variant="outline" data-testid={`badge-pending-${review.id}`}>
                          <AlertCircle className="h-3 w-3 mr-1" />
                          Pending Approval
                        </Badge>
                      )}
                    </div>
                    <CardTitle className="text-base">Creator ID: {review.creatorId}</CardTitle>
                    <CardDescription>Company ID: {review.companyId}</CardDescription>
                  </div>
                  <div className="flex gap-2">
                    {!review.isApproved && (
                      <Button
                        size="sm"
                        onClick={() => approveReviewMutation.mutate(review.id)}
                        disabled={approveReviewMutation.isPending}
                        data-testid={`button-approve-${review.id}`}
                      >
                        <CheckCircle2 className="h-4 w-4 mr-1" />
                        Approve
                      </Button>
                    )}
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleEdit(review)}
                      data-testid={`button-edit-${review.id}`}
                    >
                      Edit
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleAddNote(review)}
                      data-testid={`button-note-${review.id}`}
                    >
                      <FileText className="h-4 w-4 mr-1" />
                      Note
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => hideReviewMutation.mutate(review.id)}
                      disabled={hideReviewMutation.isPending}
                      data-testid={`button-hide-${review.id}`}
                    >
                      <EyeOff className="h-4 w-4 mr-1" />
                      {review.isHidden ? 'Hidden' : 'Hide'}
                    </Button>
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={() => {
                        if (confirm('Are you sure you want to permanently delete this review?')) {
                          deleteReviewMutation.mutate(review.id);
                        }
                      }}
                      disabled={deleteReviewMutation.isPending}
                      data-testid={`button-delete-${review.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div>
                    <h4 className="text-sm font-medium mb-2">Review Text:</h4>
                    <p className="text-sm text-muted-foreground">{review.reviewText || 'No review text'}</p>
                  </div>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div>
                      <p className="text-xs text-muted-foreground">Payment Speed</p>
                      {renderStars(review.paymentSpeedRating)}
                    </div>
                    <div>
                      <p className="text-xs text-muted-foreground">Communication</p>
                      {renderStars(review.communicationRating)}
                    </div>
                    <div>
                      <p className="text-xs text-muted-foreground">Offer Quality</p>
                      {renderStars(review.offerQualityRating)}
                    </div>
                    <div>
                      <p className="text-xs text-muted-foreground">Support</p>
                      {renderStars(review.supportRating)}
                    </div>
                  </div>
                  {review.companyResponse && (
                    <div className="bg-muted p-3 rounded-md">
                      <h4 className="text-sm font-medium mb-1">Company Response:</h4>
                      <p className="text-sm">{review.companyResponse}</p>
                    </div>
                  )}
                  {review.adminNote && (
                    <div className="bg-primary/5 p-3 rounded-md border border-primary/20">
                      <h4 className="text-sm font-medium mb-1">Admin Note:</h4>
                      <p className="text-sm">{review.adminNote}</p>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Admin Note Dialog */}
      <Dialog open={isNoteDialogOpen} onOpenChange={setIsNoteDialogOpen}>
        <DialogContent data-testid="dialog-admin-note">
          <DialogHeader>
            <DialogTitle>Admin Note</DialogTitle>
            <DialogDescription>
              Add an internal note about this review (not visible to users)
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="adminNote">Note</Label>
              <Textarea
                id="adminNote"
                value={adminNote}
                onChange={(e) => setAdminNote(e.target.value)}
                placeholder="Add your internal note here..."
                rows={4}
                data-testid="textarea-admin-note"
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setIsNoteDialogOpen(false)}
              data-testid="button-cancel-note"
            >
              Cancel
            </Button>
            <Button
              onClick={() => selectedReview && saveNoteMutation.mutate({ 
                reviewId: selectedReview.id, 
                note: adminNote 
              })}
              disabled={saveNoteMutation.isPending}
              data-testid="button-save-note"
            >
              Save Note
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Edit Review Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-2xl" data-testid="dialog-edit-review">
          <DialogHeader>
            <DialogTitle>Edit Review</DialogTitle>
            <DialogDescription>
              Modify the review content and ratings
            </DialogDescription>
          </DialogHeader>
          {editedReview && (
            <div className="space-y-4">
              <div>
                <Label htmlFor="reviewText">Review Text</Label>
                <Textarea
                  id="reviewText"
                  value={editedReview.reviewText}
                  onChange={(e) => setEditedReview({ ...editedReview, reviewText: e.target.value })}
                  placeholder="Review text..."
                  rows={4}
                  data-testid="textarea-review-text"
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Overall Rating: {editedReview.overallRating}</Label>
                  <input
                    type="range"
                    min="1"
                    max="5"
                    value={editedReview.overallRating}
                    onChange={(e) => setEditedReview({ ...editedReview, overallRating: parseInt(e.target.value) })}
                    className="w-full"
                    data-testid="input-overall-rating"
                  />
                </div>
                <div>
                  <Label>Payment Speed: {editedReview.paymentSpeedRating}</Label>
                  <input
                    type="range"
                    min="1"
                    max="5"
                    value={editedReview.paymentSpeedRating}
                    onChange={(e) => setEditedReview({ ...editedReview, paymentSpeedRating: parseInt(e.target.value) })}
                    className="w-full"
                    data-testid="input-payment-speed-rating"
                  />
                </div>
                <div>
                  <Label>Communication: {editedReview.communicationRating}</Label>
                  <input
                    type="range"
                    min="1"
                    max="5"
                    value={editedReview.communicationRating}
                    onChange={(e) => setEditedReview({ ...editedReview, communicationRating: parseInt(e.target.value) })}
                    className="w-full"
                    data-testid="input-communication-rating"
                  />
                </div>
                <div>
                  <Label>Offer Quality: {editedReview.offerQualityRating}</Label>
                  <input
                    type="range"
                    min="1"
                    max="5"
                    value={editedReview.offerQualityRating}
                    onChange={(e) => setEditedReview({ ...editedReview, offerQualityRating: parseInt(e.target.value) })}
                    className="w-full"
                    data-testid="input-offer-quality-rating"
                  />
                </div>
                <div>
                  <Label>Support: {editedReview.supportRating}</Label>
                  <input
                    type="range"
                    min="1"
                    max="5"
                    value={editedReview.supportRating}
                    onChange={(e) => setEditedReview({ ...editedReview, supportRating: parseInt(e.target.value) })}
                    className="w-full"
                    data-testid="input-support-rating"
                  />
                </div>
              </div>
            </div>
          )}
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setIsEditDialogOpen(false)}
              data-testid="button-cancel-edit"
            >
              Cancel
            </Button>
            <Button
              onClick={() => selectedReview && editedReview && updateReviewMutation.mutate({
                reviewId: selectedReview.id,
                updates: { ...editedReview, isEdited: true }
              })}
              disabled={updateReviewMutation.isPending}
              data-testid="button-save-edit"
            >
              Save Changes
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/admin-companies.tsx.content:
import { useEffect, useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Building2, CheckCircle2, XCircle } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";

export default function AdminCompanies() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [selectedCompany, setSelectedCompany] = useState<any>(null);
  const [isRejectDialogOpen, setIsRejectDialogOpen] = useState(false);
  const [rejectionReason, setRejectionReason] = useState("");

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: companies = [], isLoading: loadingCompanies } = useQuery<any[]>({
    queryKey: ["/api/admin/companies"],
    enabled: isAuthenticated,
  });

  const approveMutation = useMutation({
    mutationFn: async (companyId: string) => {
      const response = await apiRequest("POST", `/api/admin/companies/${companyId}/approve`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/companies"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/stats"] });
      toast({
        title: "Success",
        description: "Company approved successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to approve company",
        variant: "destructive",
      });
    },
  });

  const rejectMutation = useMutation({
    mutationFn: async ({ companyId, reason }: { companyId: string; reason: string }) => {
      const response = await apiRequest("POST", `/api/admin/companies/${companyId}/reject`, { reason });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/companies"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/stats"] });
      toast({
        title: "Success",
        description: "Company rejected",
      });
      setIsRejectDialogOpen(false);
      setRejectionReason("");
      setSelectedCompany(null);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reject company",
        variant: "destructive",
      });
    },
  });

  const handleReject = (company: any) => {
    setSelectedCompany(company);
    setIsRejectDialogOpen(true);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-bold">Company Approvals</h1>
        <p className="text-muted-foreground mt-1">
          Review and approve companies to join the platform
        </p>
      </div>

      {loadingCompanies ? (
        <div className="text-center py-12">
          <div className="animate-pulse text-lg text-muted-foreground">
            Loading companies...
          </div>
        </div>
      ) : companies.length === 0 ? (
        <Card className="border-card-border">
          <CardContent className="flex flex-col items-center justify-center py-12">
            <CheckCircle2 className="h-12 w-12 text-muted-foreground/50 mb-4" />
            <h3 className="text-lg font-semibold mb-2">All caught up!</h3>
            <p className="text-sm text-muted-foreground text-center max-w-md">
              No pending companies to review at this time
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {companies.map((company: any) => (
            <Card key={company.id} className="border-card-border" data-testid={`card-company-${company.id}`}>
              <CardHeader>
                <div className="flex items-start justify-between gap-2">
                  <div className="flex items-start gap-3 flex-1 min-w-0">
                    <div className="flex items-center justify-center w-10 h-10 rounded-lg bg-primary/10 shrink-0">
                      <Building2 className="h-5 w-5 text-primary" />
                    </div>
                    <div className="flex-1 min-w-0">
                      <CardTitle className="text-lg line-clamp-1">{company.legalName}</CardTitle>
                      <Badge 
                        variant={company.status === 'approved' ? 'default' : 'secondary'}
                        className="mt-2"
                        data-testid={`badge-status-${company.id}`}
                      >
                        {company.status}
                      </Badge>
                    </div>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                {company.websiteUrl && (
                  <div>
                    <div className="text-xs text-muted-foreground mb-1">Website</div>
                    <a 
                      href={company.websiteUrl} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="text-sm text-primary hover:underline break-all"
                      data-testid={`link-website-${company.id}`}
                    >
                      {company.websiteUrl}
                    </a>
                  </div>
                )}

                {company.description && (
                  <div>
                    <div className="text-xs text-muted-foreground mb-1">Description</div>
                    <p className="text-sm line-clamp-3">{company.description}</p>
                  </div>
                )}

                <div className="grid grid-cols-2 gap-2 pt-3 border-t">
                  <Button
                    variant="default"
                    size="sm"
                    className="gap-2"
                    onClick={() => approveMutation.mutate(company.id)}
                    disabled={approveMutation.isPending || company.status === 'approved'}
                    data-testid={`button-approve-${company.id}`}
                  >
                    <CheckCircle2 className="h-4 w-4" />
                    Approve
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    className="gap-2"
                    onClick={() => handleReject(company)}
                    disabled={rejectMutation.isPending || company.status === 'rejected'}
                    data-testid={`button-reject-${company.id}`}
                  >
                    <XCircle className="h-4 w-4" />
                    Reject
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Rejection Dialog */}
      <Dialog open={isRejectDialogOpen} onOpenChange={setIsRejectDialogOpen}>
        <DialogContent data-testid="dialog-reject-company">
          <DialogHeader>
            <DialogTitle>Reject Company</DialogTitle>
            <DialogDescription>
              Provide a reason for rejecting this company registration
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="reason">Rejection Reason</Label>
              <Textarea
                id="reason"
                placeholder="Enter reason for rejection..."
                value={rejectionReason}
                onChange={(e) => setRejectionReason(e.target.value)}
                className="min-h-[100px]"
                data-testid="textarea-rejection-reason"
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setIsRejectDialogOpen(false);
                setRejectionReason("");
                setSelectedCompany(null);
              }}
              data-testid="button-cancel-reject"
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={() => {
                if (selectedCompany && rejectionReason.trim()) {
                  rejectMutation.mutate({ 
                    companyId: selectedCompany.id, 
                    reason: rejectionReason 
                  });
                }
              }}
              disabled={!rejectionReason.trim() || rejectMutation.isPending}
              data-testid="button-confirm-reject"
            >
              {rejectMutation.isPending ? "Rejecting..." : "Reject Company"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/company-offer-detail.ts.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute, Link } from "wouter";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, DollarSign, Users, Eye, Calendar, Upload, Trash2, Video, AlertCircle } from "lucide-react";
import { ObjectUploader } from "@/components/ObjectUploader";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Alert, AlertDescription } from "@/components/ui/alert";

export default function CompanyOfferDetail() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [, params] = useRoute("/company/offers/:id");
  const offerId = params?.id;

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: offer, isLoading: offerLoading } = useQuery<any>({
    queryKey: [`/api/offers/${offerId}`],
    enabled: !!offerId && isAuthenticated,
  });

  const { data: applications = [] } = useQuery<any[]>({
    queryKey: ["/api/company/applications"],
    enabled: isAuthenticated,
  });

  const { data: videos = [], isLoading: videosLoading } = useQuery<any[]>({
    queryKey: [`/api/offers/${offerId}/videos`],
    enabled: !!offerId && isAuthenticated,
  });

  // Filter applications for this offer
  const offerApplications = applications.filter((app: any) => app.offerId === offerId);

  // Video upload state
  const [showVideoDialog, setShowVideoDialog] = useState(false);
  const [videoUrl, setVideoUrl] = useState("");
  const [videoTitle, setVideoTitle] = useState("");
  const [videoDescription, setVideoDescription] = useState("");
  const [creatorCredit, setCreatorCredit] = useState("");
  const [originalPlatform, setOriginalPlatform] = useState("");

  const createVideoMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", `/api/offers/${offerId}/videos`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/offers/${offerId}/videos`] });
      setShowVideoDialog(false);
      setVideoUrl("");
      setVideoTitle("");
      setVideoDescription("");
      setCreatorCredit("");
      setOriginalPlatform("");
      toast({
        title: "Success",
        description: "Video added successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to add video",
        variant: "destructive",
      });
    },
  });

  const deleteVideoMutation = useMutation({
    mutationFn: async (videoId: string) => {
      return await apiRequest("DELETE", `/api/offer-videos/${videoId}`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/offers/${offerId}/videos`] });
      toast({
        title: "Success",
        description: "Video deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete video",
        variant: "destructive",
      });
    },
  });

  const handleGetUploadUrl = async () => {
    const response = await fetch("/api/objects/upload", {
      method: "POST",
      credentials: "include",
    });
    const data = await response.json();
    return {
      method: "PUT" as const,
      url: data.uploadURL,
    };
  };

  const handleUploadComplete = (result: any) => {
    if (result.successful && result.successful[0]) {
      const uploadedUrl = result.successful[0].uploadURL.split("?")[0];
      setVideoUrl(uploadedUrl);
    }
  };

  const handleSubmitVideo = () => {
    if (!videoUrl || !videoTitle) {
      toast({
        title: "Error",
        description: "Video URL and title are required",
        variant: "destructive",
      });
      return;
    }

    createVideoMutation.mutate({
      videoUrl,
      title: videoTitle,
      description: videoDescription,
      creatorCredit,
      originalPlatform,
    });
  };

  const videoCount = videos.length;
  const canAddMoreVideos = videoCount < 12;
  const hasMinimumVideos = videoCount >= 6;

  if (isLoading || offerLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  if (!offer) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen gap-4">
        <h2 className="text-2xl font-bold">Offer not found</h2>
        <Link href="/company/offers">
          <Button>Back to My Offers</Button>
        </Link>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center gap-4">
        <Link href="/company/offers">
          <Button variant="ghost" size="icon" data-testid="button-back">
            <ArrowLeft className="h-5 w-5" />
          </Button>
        </Link>
        <div className="flex-1">
          <h1 className="text-3xl font-bold">{offer.title}</h1>
          <p className="text-muted-foreground mt-1">{offer.productName}</p>
        </div>
        <Badge
          variant={offer.status === 'approved' ? 'default' : 'secondary'}
          data-testid="badge-status"
        >
          {offer.status}
        </Badge>
      </div>

      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Applications</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{offerApplications.length}</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">View Count</CardTitle>
            <Eye className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{offer.viewCount || 0}</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Commission</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {offer.commissionPercentage ? `${offer.commissionPercentage}%` : 
               offer.commissionAmount ? `$${offer.commissionAmount}` : 'N/A'}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Created</CardTitle>
            <Calendar className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-sm font-bold">
              {new Date(offer.createdAt).toLocaleDateString()}
            </div>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Offer Details</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <h3 className="font-semibold mb-2">Short Description</h3>
            <p className="text-muted-foreground">{offer.shortDescription}</p>
          </div>

          <div>
            <h3 className="font-semibold mb-2">Full Description</h3>
            <p className="text-muted-foreground whitespace-pre-wrap">{offer.fullDescription}</p>
          </div>

          <div className="grid gap-4 md:grid-cols-2">
            <div>
              <h3 className="font-semibold mb-2">Primary Niche</h3>
              <Badge variant="outline">{offer.primaryNiche}</Badge>
            </div>

            <div>
              <h3 className="font-semibold mb-2">Commission Type</h3>
              <Badge variant="outline">{offer.commissionType?.replace(/_/g, ' ')}</Badge>
            </div>
          </div>

          <div>
            <h3 className="font-semibold mb-2">Product URL</h3>
            <a 
              href={offer.productUrl} 
              target="_blank" 
              rel="noopener noreferrer"
              className="text-primary hover:underline"
            >
              {offer.productUrl}
            </a>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Video className="h-5 w-5" />
            Promotional Videos
          </CardTitle>
          <CardDescription>
            Upload 6-12 videos showcasing your product. Videos help creators understand and promote your offer.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {!hasMinimumVideos && (
            <Alert variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                You need at least 6 videos to publish this offer. Currently: {videoCount}/6
              </AlertDescription>
            </Alert>
          )}

          <div className="flex items-center justify-between">
            <div className="text-sm text-muted-foreground">
              {videoCount} of 12 videos uploaded
            </div>
            <Button
              onClick={() => setShowVideoDialog(true)}
              disabled={!canAddMoreVideos || createVideoMutation.isPending}
              data-testid="button-add-video"
            >
              <Upload className="h-4 w-4 mr-2" />
              Add Video
            </Button>
          </div>

          {videosLoading ? (
            <div className="text-center py-8 text-muted-foreground">Loading videos...</div>
          ) : videos.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              No videos uploaded yet. Add your first video to get started.
            </div>
          ) : (
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
              {videos.map((video: any) => (
                <Card key={video.id} className="overflow-hidden">
                  <CardContent className="p-4 space-y-2">
                    <div className="aspect-video bg-muted rounded-md flex items-center justify-center">
                      <Video className="h-8 w-8 text-muted-foreground" />
                    </div>
                    <div>
                      <h4 className="font-semibold text-sm line-clamp-1" data-testid={`text-video-title-${video.id}`}>
                        {video.title}
                      </h4>
                      {video.description && (
                        <p className="text-xs text-muted-foreground line-clamp-2">
                          {video.description}
                        </p>
                      )}
                      {video.creatorCredit && (
                        <p className="text-xs text-muted-foreground mt-1">
                          Credit: {video.creatorCredit}
                        </p>
                      )}
                    </div>
                    <Button
                      variant="destructive"
                      size="sm"
                      className="w-full"
                      onClick={() => deleteVideoMutation.mutate(video.id)}
                      disabled={deleteVideoMutation.isPending}
                      data-testid={`button-delete-video-${video.id}`}
                    >
                      <Trash2 className="h-3 w-3 mr-2" />
                      Delete
                    </Button>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      <Dialog open={showVideoDialog} onOpenChange={setShowVideoDialog}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Add Promotional Video</DialogTitle>
            <DialogDescription>
              Upload a video that showcases your product or service
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label>Video File</Label>
              <div className="mt-2">
                <ObjectUploader
                  maxNumberOfFiles={1}
                  maxFileSize={524288000}
                  onGetUploadParameters={handleGetUploadUrl}
                  onComplete={handleUploadComplete}
                >
                  {videoUrl ? "Video Uploaded " : "Upload Video"}
                </ObjectUploader>
              </div>
              {videoUrl && (
                <p className="text-xs text-muted-foreground mt-1">
                  Video uploaded successfully
                </p>
              )}
            </div>

            <div>
              <Label htmlFor="video-title">Title *</Label>
              <Input
                id="video-title"
                value={videoTitle}
                onChange={(e) => setVideoTitle(e.target.value)}
                placeholder="e.g., Product Demo"
                data-testid="input-video-title"
              />
            </div>

            <div>
              <Label htmlFor="video-description">Description</Label>
              <Textarea
                id="video-description"
                value={videoDescription}
                onChange={(e) => setVideoDescription(e.target.value)}
                placeholder="Brief description of the video"
                rows={3}
                data-testid="input-video-description"
              />
            </div>

            <div>
              <Label htmlFor="creator-credit">Creator Credit</Label>
              <Input
                id="creator-credit"
                value={creatorCredit}
                onChange={(e) => setCreatorCredit(e.target.value)}
                placeholder="e.g., @username"
                data-testid="input-creator-credit"
              />
            </div>

            <div>
              <Label htmlFor="original-platform">Original Platform</Label>
              <Input
                id="original-platform"
                value={originalPlatform}
                onChange={(e) => setOriginalPlatform(e.target.value)}
                placeholder="e.g., TikTok, Instagram, YouTube"
                data-testid="input-original-platform"
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setShowVideoDialog(false)}
              data-testid="button-cancel-video"
            >
              Cancel
            </Button>
            <Button
              onClick={handleSubmitVideo}
              disabled={!videoUrl || !videoTitle || createVideoMutation.isPending}
              data-testid="button-submit-video"
            >
              {createVideoMutation.isPending ? "Adding..." : "Add Video"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {offerApplications.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Recent Applications</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {offerApplications.slice(0, 5).map((app: any) => (
                <div key={app.id} className="flex items-center justify-between p-3 bg-muted/50 rounded-md">
                  <div>
                    <p className="font-medium">{app.creatorName || 'Creator'}</p>
                    <p className="text-sm text-muted-foreground">
                      {new Date(app.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                  <Badge variant={app.status === 'approved' ? 'default' : 'secondary'}>
                    {app.status}
                  </Badge>
                </div>
              ))}
            </div>
            {offerApplications.length > 5 && (
              <Link href="/company/applications">
                <Button variant="outline" className="w-full mt-4" data-testid="button-view-all">
                  View All Applications
                </Button>
              </Link>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/admin-offers.tsx.content:
import { useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { TrendingUp, CheckCircle2, XCircle, DollarSign } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";

export default function AdminOffers() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: offers = [], isLoading: loadingOffers } = useQuery<any[]>({
    queryKey: ["/api/admin/offers"],
    enabled: isAuthenticated,
  });

  const approveMutation = useMutation({
    mutationFn: async (offerId: string) => {
      const response = await apiRequest("POST", `/api/admin/offers/${offerId}/approve`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/offers"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/stats"] });
      toast({
        title: "Success",
        description: "Offer approved successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to approve offer",
        variant: "destructive",
      });
    },
  });

  const rejectMutation = useMutation({
    mutationFn: async (offerId: string) => {
      const response = await apiRequest("POST", `/api/admin/offers/${offerId}/reject`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/offers"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/stats"] });
      toast({
        title: "Success",
        description: "Offer rejected",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reject offer",
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-bold">Offer Approvals</h1>
        <p className="text-muted-foreground mt-1">
          Review and approve affiliate offers
        </p>
      </div>

      {loadingOffers ? (
        <div className="text-center py-12">
          <div className="animate-pulse text-lg text-muted-foreground">
            Loading offers...
          </div>
        </div>
      ) : offers.length === 0 ? (
        <Card className="border-card-border">
          <CardContent className="flex flex-col items-center justify-center py-12">
            <CheckCircle2 className="h-12 w-12 text-muted-foreground/50 mb-4" />
            <h3 className="text-lg font-semibold mb-2">All caught up!</h3>
            <p className="text-sm text-muted-foreground text-center max-w-md">
              No pending offers to review at this time
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {offers.map((offer: any) => (
            <Card key={offer.id} className="border-card-border" data-testid={`card-offer-${offer.id}`}>
              <CardHeader>
                <div className="flex items-start justify-between gap-2">
                  <div className="flex items-start gap-3 flex-1 min-w-0">
                    <div className="flex items-center justify-center w-10 h-10 rounded-lg bg-primary/10 shrink-0">
                      <TrendingUp className="h-5 w-5 text-primary" />
                    </div>
                    <div className="flex-1 min-w-0">
                      <CardTitle className="text-lg line-clamp-1">{offer.title}</CardTitle>
                      <div className="flex items-center gap-2 mt-2">
                        <Badge 
                          variant={offer.status === 'approved' ? 'default' : 'secondary'}
                          data-testid={`badge-status-${offer.id}`}
                        >
                          {offer.status}
                        </Badge>
                        {offer.primaryNiche && (
                          <Badge variant="outline" className="text-xs">
                            {offer.primaryNiche}
                          </Badge>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                <p className="text-sm text-muted-foreground line-clamp-2">
                  {offer.shortDescription || offer.description || "No description"}
                </p>

                {offer.productUrl && (
                  <div>
                    <div className="text-xs text-muted-foreground mb-1">Product URL</div>
                    <a 
                      href={offer.productUrl} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="text-xs text-primary hover:underline break-all"
                      data-testid={`link-product-${offer.id}`}
                    >
                      {offer.productUrl}
                    </a>
                  </div>
                )}

                <div className="flex items-center gap-4 pt-3 border-t">
                  <div>
                    <div className="text-xs text-muted-foreground mb-1">Commission</div>
                    <div className="text-sm font-semibold flex items-center gap-1">
                      <DollarSign className="h-3 w-3" />
                      {offer.commissionPercentage || offer.commissionAmount || offer.commissionRate || 0}
                      {offer.commissionType === 'per_sale' && '%'}
                    </div>
                  </div>
                  <div>
                    <div className="text-xs text-muted-foreground mb-1">Type</div>
                    <div className="text-sm">
                      {offer.commissionType?.replace(/_/g, ' ') || 'N/A'}
                    </div>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-2 pt-3 border-t">
                  <Button
                    variant="default"
                    size="sm"
                    className="gap-2"
                    onClick={() => approveMutation.mutate(offer.id)}
                    disabled={approveMutation.isPending || offer.status === 'approved'}
                    data-testid={`button-approve-${offer.id}`}
                  >
                    <CheckCircle2 className="h-4 w-4" />
                    Approve
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    className="gap-2"
                    onClick={() => rejectMutation.mutate(offer.id)}
                    disabled={rejectMutation.isPending || offer.status === 'rejected'}
                    data-testid={`button-reject-${offer.id}`}
                  >
                    <XCircle className="h-4 w-4" />
                    Reject
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/pages/company-retainer-detail.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import {
  DollarSign,
  Video,
  Calendar,
  Check,
  X,
  ArrowLeft,
  ExternalLink,
  User,
  Play,
  AlertCircle,
} from "lucide-react";
import { Link } from "wouter";
import { format } from "date-fns";
import { useState } from "react";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";

export default function CompanyRetainerDetail() {
  const [, params] = useRoute("/company/retainers/:id");
  const { toast } = useToast();
  const contractId = params?.id;
  const [reviewNotes, setReviewNotes] = useState<Record<string, string>>({});

  const { data: contract, isLoading } = useQuery<any>({
    queryKey: ["/api/retainer-contracts", contractId],
    enabled: !!contractId,
  });

  const { data: applications } = useQuery<any[]>({
    queryKey: ["/api/retainer-contracts", contractId, "applications"],
    enabled: !!contractId,
  });

  const { data: deliverables } = useQuery<any[]>({
    queryKey: ["/api/retainer-contracts", contractId, "deliverables"],
    enabled: !!contractId,
  });

  const approveMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      return await apiRequest("POST", `/api/retainer-applications/${applicationId}/approve`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/retainer-contracts", contractId, "applications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/retainer-contracts", contractId] });
      toast({
        title: "Application Approved",
        description: "The creator has been assigned to this retainer contract.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to approve application",
        variant: "destructive",
      });
    },
  });

  const rejectMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      return await apiRequest("POST", `/api/retainer-applications/${applicationId}/reject`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/retainer-contracts", contractId, "applications"] });
      toast({
        title: "Application Rejected",
        description: "The application has been rejected.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reject application",
        variant: "destructive",
      });
    },
  });

  const approveDeliverableMutation = useMutation({
    mutationFn: async ({ id, notes }: { id: string; notes?: string }) => {
      return await apiRequest("POST", `/api/retainer-deliverables/${id}/approve`, {
        reviewNotes: notes,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/retainer-contracts", contractId, "deliverables"] });
      toast({
        title: "Deliverable Approved",
        description: "The video has been approved.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to approve deliverable",
        variant: "destructive",
      });
    },
  });

  const requestRevisionMutation = useMutation({
    mutationFn: async ({ id, notes }: { id: string; notes: string }) => {
      return await apiRequest("POST", `/api/retainer-deliverables/${id}/request-revision`, {
        reviewNotes: notes,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/retainer-contracts", contractId, "deliverables"] });
      toast({
        title: "Revision Requested",
        description: "The creator has been notified to revise the video.",
      });
      setReviewNotes({});
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to request revision",
        variant: "destructive",
      });
    },
  });

  const rejectDeliverableMutation = useMutation({
    mutationFn: async ({ id, notes }: { id: string; notes: string }) => {
      return await apiRequest("POST", `/api/retainer-deliverables/${id}/reject`, {
        reviewNotes: notes,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/retainer-contracts", contractId, "deliverables"] });
      toast({
        title: "Deliverable Rejected",
        description: "The video has been rejected.",
      });
      setReviewNotes({});
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reject deliverable",
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return <div className="space-y-6">Loading...</div>;
  }

  if (!contract) {
    return <div className="space-y-6">Contract not found</div>;
  }

  const getStatusBadgeVariant = (status: string) => {
    switch (status) {
      case "open":
        return "default";
      case "in_progress":
        return "secondary";
      case "completed":
        return "outline";
      case "cancelled":
        return "destructive";
      default:
        return "secondary";
    }
  };

  const getApplicationStatusBadgeVariant = (status: string) => {
    switch (status) {
      case "pending":
        return "default";
      case "approved":
        return "outline";
      case "rejected":
        return "destructive";
      default:
        return "secondary";
    }
  };

  const pendingApplications = applications?.filter((app: any) => app.status === "pending") || [];
  const approvedApplications = applications?.filter((app: any) => app.status === "approved") || [];
  const rejectedApplications = applications?.filter((app: any) => app.status === "rejected") || [];

  const pendingDeliverables = deliverables?.filter((d: any) => d.status === "pending_review") || [];
  const approvedDeliverables = deliverables?.filter((d: any) => d.status === "approved") || [];
  const revisionDeliverables = deliverables?.filter((d: any) => d.status === "revision_requested") || [];
  const rejectedDeliverables = deliverables?.filter((d: any) => d.status === "rejected") || [];

  const getDeliverableStatusBadgeVariant = (status: string) => {
    switch (status) {
      case "pending_review":
        return "default";
      case "approved":
        return "outline";
      case "revision_requested":
        return "secondary";
      case "rejected":
        return "destructive";
      default:
        return "secondary";
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center gap-4">
        <Link href="/company/retainers">
          <Button variant="ghost" size="icon" data-testid="button-back">
            <ArrowLeft className="h-4 w-4" />
          </Button>
        </Link>
        <div className="flex-1">
          <div className="flex items-center gap-2 mb-1">
            <h1 className="text-3xl font-bold" data-testid="heading-contract-title">
              {contract.title}
            </h1>
            <Badge variant={getStatusBadgeVariant(contract.status)}>
              {contract.status.replace("_", " ")}
            </Badge>
          </div>
          <p className="text-muted-foreground">Retainer Contract Details</p>
        </div>
      </div>

      <div className="grid lg:grid-cols-3 gap-6">
        <Card className="border-card-border">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-muted-foreground">
              Monthly Payment
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-3">
              <DollarSign className="h-8 w-8 text-primary" />
              <div className="text-2xl font-bold">
                ${parseFloat(contract.monthlyAmount).toLocaleString()}
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="border-card-border">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-muted-foreground">
              Videos Per Month
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-3">
              <Video className="h-8 w-8 text-primary" />
              <div className="text-2xl font-bold">{contract.videosPerMonth}</div>
            </div>
          </CardContent>
        </Card>

        <Card className="border-card-border">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-muted-foreground">
              Contract Duration
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-3">
              <Calendar className="h-8 w-8 text-primary" />
              <div className="text-2xl font-bold">{contract.durationMonths} months</div>
            </div>
          </CardContent>
        </Card>
      </div>

      <Tabs defaultValue="details" className="space-y-6">
        <TabsList>
          <TabsTrigger value="details">Details</TabsTrigger>
          <TabsTrigger value="applications" data-testid="tab-applications">
            Applications ({applications?.length || 0})
          </TabsTrigger>
          <TabsTrigger value="deliverables" data-testid="tab-deliverables">
            Deliverables ({deliverables?.length || 0})
          </TabsTrigger>
        </TabsList>

        <TabsContent value="details" className="space-y-6">
          <Card className="border-card-border">
            <CardHeader>
              <CardTitle>Contract Information</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <h4 className="font-semibold mb-2">Description</h4>
                <p className="text-muted-foreground">{contract.description}</p>
              </div>

              <div className="grid sm:grid-cols-2 gap-4 pt-4 border-t">
                <div>
                  <h4 className="font-semibold mb-1">Required Platform</h4>
                  <p className="text-muted-foreground">{contract.requiredPlatform}</p>
                </div>
                {contract.minimumFollowers && (
                  <div>
                    <h4 className="font-semibold mb-1">Minimum Followers</h4>
                    <p className="text-muted-foreground">
                      {contract.minimumFollowers.toLocaleString()}
                    </p>
                  </div>
                )}
              </div>

              {contract.platformAccountDetails && (
                <div className="pt-4 border-t">
                  <h4 className="font-semibold mb-2">Platform Account Details</h4>
                  <p className="text-muted-foreground">{contract.platformAccountDetails}</p>
                </div>
              )}

              {contract.contentGuidelines && (
                <div className="pt-4 border-t">
                  <h4 className="font-semibold mb-2">Content Guidelines</h4>
                  <p className="text-muted-foreground">{contract.contentGuidelines}</p>
                </div>
              )}

              {contract.brandSafetyRequirements && (
                <div className="pt-4 border-t">
                  <h4 className="font-semibold mb-2">Brand Safety Requirements</h4>
                  <p className="text-muted-foreground">{contract.brandSafetyRequirements}</p>
                </div>
              )}

              {contract.niches && contract.niches.length > 0 && (
                <div className="pt-4 border-t">
                  <h4 className="font-semibold mb-2">Target Niches</h4>
                  <div className="flex gap-2 flex-wrap">
                    {contract.niches.map((niche: string, index: number) => (
                      <Badge key={index} variant="outline">
                        {niche}
                      </Badge>
                    ))}
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="applications" className="space-y-6">
          {pendingApplications.length > 0 && (
            <div className="space-y-4">
              <h3 className="font-semibold">Pending Applications ({pendingApplications.length})</h3>
              {pendingApplications.map((app: any) => (
                <Card key={app.id} className="border-card-border" data-testid={`application-card-${app.id}`}>
                  <CardHeader>
                    <div className="flex items-start justify-between gap-4">
                      <div className="flex items-center gap-3">
                        <Avatar>
                          <AvatarImage src={app.creator?.avatarUrl} />
                          <AvatarFallback>
                            {app.creator?.username?.[0]?.toUpperCase() || "C"}
                          </AvatarFallback>
                        </Avatar>
                        <div>
                          <CardTitle className="text-base">
                            {app.creator?.username || "Unknown Creator"}
                          </CardTitle>
                          <p className="text-sm text-muted-foreground">
                            Applied {format(new Date(app.createdAt), "MMM d, yyyy")}
                          </p>
                        </div>
                      </div>
                      <Badge variant={getApplicationStatusBadgeVariant(app.status)}>
                        {app.status}
                      </Badge>
                    </div>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div>
                      <h4 className="font-semibold text-sm mb-2">Message</h4>
                      <p className="text-sm text-muted-foreground">{app.message}</p>
                    </div>

                    {app.portfolioLinks && app.portfolioLinks.length > 0 && (
                      <div>
                        <h4 className="font-semibold text-sm mb-2">Portfolio Links</h4>
                        <div className="flex flex-col gap-1">
                          {app.portfolioLinks.map((link: string, index: number) => (
                            <a
                              key={index}
                              href={link}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-sm text-primary hover:underline flex items-center gap-1"
                            >
                              <ExternalLink className="h-3 w-3" />
                              {link}
                            </a>
                          ))}
                        </div>
                      </div>
                    )}

                    {app.proposedStartDate && (
                      <div>
                        <h4 className="font-semibold text-sm mb-1">Proposed Start Date</h4>
                        <p className="text-sm text-muted-foreground">
                          {format(new Date(app.proposedStartDate), "MMMM d, yyyy")}
                        </p>
                      </div>
                    )}

                    <div className="flex gap-2 pt-4 border-t">
                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button
                            variant="default"
                            className="flex-1"
                            data-testid={`button-approve-${app.id}`}
                          >
                            <Check className="h-4 w-4 mr-2" />
                            Approve
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Approve Application?</AlertDialogTitle>
                            <AlertDialogDescription>
                              This will assign {app.creator?.username} to the retainer contract and change the status to "In Progress".
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                            <AlertDialogAction onClick={() => approveMutation.mutate(app.id)}>
                              Approve
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>

                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button
                            variant="outline"
                            className="flex-1"
                            data-testid={`button-reject-${app.id}`}
                          >
                            <X className="h-4 w-4 mr-2" />
                            Reject
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Reject Application?</AlertDialogTitle>
                            <AlertDialogDescription>
                              This will reject the application from {app.creator?.username}.
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => rejectMutation.mutate(app.id)}
                              className="bg-destructive hover:bg-destructive/90"
                            >
                              Reject
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {approvedApplications.length > 0 && (
            <div className="space-y-4">
              <h3 className="font-semibold">Approved ({approvedApplications.length})</h3>
              {approvedApplications.map((app: any) => (
                <Card key={app.id} className="border-card-border">
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <Avatar>
                          <AvatarImage src={app.creator?.avatarUrl} />
                          <AvatarFallback>
                            {app.creator?.username?.[0]?.toUpperCase() || "C"}
                          </AvatarFallback>
                        </Avatar>
                        <div>
                          <p className="font-semibold">{app.creator?.username}</p>
                          <p className="text-sm text-muted-foreground">
                            Approved {format(new Date(app.updatedAt), "MMM d, yyyy")}
                          </p>
                        </div>
                      </div>
                      <Badge variant="outline">Approved</Badge>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {rejectedApplications.length > 0 && (
            <div className="space-y-4">
              <h3 className="font-semibold text-muted-foreground">
                Rejected ({rejectedApplications.length})
              </h3>
              {rejectedApplications.map((app: any) => (
                <Card key={app.id} className="border-card-border opacity-60">
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <Avatar>
                          <AvatarImage src={app.creator?.avatarUrl} />
                          <AvatarFallback>
                            {app.creator?.username?.[0]?.toUpperCase() || "C"}
                          </AvatarFallback>
                        </Avatar>
                        <div>
                          <p className="font-semibold">{app.creator?.username}</p>
                          <p className="text-sm text-muted-foreground">
                            Rejected {format(new Date(app.updatedAt), "MMM d, yyyy")}
                          </p>
                        </div>
                      </div>
                      <Badge variant="destructive">Rejected</Badge>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {!applications || applications.length === 0 && (
            <Card className="border-card-border">
              <CardContent className="p-12 text-center">
                <User className="h-12 w-12 text-muted-foreground/50 mx-auto mb-4" />
                <p className="text-muted-foreground">No applications received yet</p>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="deliverables" className="space-y-6">
          {pendingDeliverables.length > 0 && (
            <div className="space-y-4">
              <h3 className="font-semibold">Pending Review ({pendingDeliverables.length})</h3>
              {pendingDeliverables.map((deliverable: any) => (
                <Card key={deliverable.id} className="border-card-border" data-testid={`deliverable-card-${deliverable.id}`}>
                  <CardHeader>
                    <div className="flex items-start justify-between gap-4">
                      <div>
                        <CardTitle className="text-base">{deliverable.title}</CardTitle>
                        <p className="text-sm text-muted-foreground">
                          Month {deliverable.monthNumber} - Video #{deliverable.videoNumber}
                        </p>
                        <p className="text-xs text-muted-foreground mt-1">
                          Submitted {format(new Date(deliverable.submittedAt), "MMM d, yyyy")}
                        </p>
                      </div>
                      <Badge variant={getDeliverableStatusBadgeVariant(deliverable.status)}>
                        Pending Review
                      </Badge>
                    </div>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {deliverable.description && (
                      <p className="text-sm text-muted-foreground">{deliverable.description}</p>
                    )}

                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => window.open(deliverable.videoUrl, "_blank")}
                        data-testid={`button-view-video-${deliverable.id}`}
                      >
                        <Play className="h-3 w-3 mr-1" />
                        View Video
                      </Button>
                      {deliverable.platformUrl && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => window.open(deliverable.platformUrl, "_blank")}
                        >
                          <ExternalLink className="h-3 w-3 mr-1" />
                          View on Platform
                        </Button>
                      )}
                    </div>

                    <div className="pt-4 border-t space-y-3">
                      <label className="text-sm font-semibold">Review Notes (Optional)</label>
                      <Textarea
                        placeholder="Add feedback or notes about this video..."
                        rows={2}
                        value={reviewNotes[deliverable.id] || ""}
                        onChange={(e) =>
                          setReviewNotes({ ...reviewNotes, [deliverable.id]: e.target.value })
                        }
                        data-testid={`input-review-notes-${deliverable.id}`}
                      />
                      <div className="flex gap-2">
                        <Button
                          variant="default"
                          size="sm"
                          onClick={() =>
                            approveDeliverableMutation.mutate({
                              id: deliverable.id,
                              notes: reviewNotes[deliverable.id],
                            })
                          }
                          data-testid={`button-approve-deliverable-${deliverable.id}`}
                        >
                          <Check className="h-3 w-3 mr-1" />
                          Approve
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => {
                            if (!reviewNotes[deliverable.id]) {
                              toast({
                                title: "Notes Required",
                                description: "Please add notes explaining what needs to be revised.",
                                variant: "destructive",
                              });
                              return;
                            }
                            requestRevisionMutation.mutate({
                              id: deliverable.id,
                              notes: reviewNotes[deliverable.id],
                            });
                          }}
                          data-testid={`button-request-revision-${deliverable.id}`}
                        >
                          <AlertCircle className="h-3 w-3 mr-1" />
                          Request Revision
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => {
                            if (!reviewNotes[deliverable.id]) {
                              toast({
                                title: "Notes Required",
                                description: "Please add notes explaining why this is being rejected.",
                                variant: "destructive",
                              });
                              return;
                            }
                            rejectDeliverableMutation.mutate({
                              id: deliverable.id,
                              notes: reviewNotes[deliverable.id],
                            });
                          }}
                          data-testid={`button-reject-deliverable-${deliverable.id}`}
                        >
                          <X className="h-3 w-3 mr-1" />
                          Reject
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {approvedDeliverables.length > 0 && (
            <div className="space-y-4">
              <h3 className="font-semibold">Approved ({approvedDeliverables.length})</h3>
              <div className="grid sm:grid-cols-2 gap-4">
                {approvedDeliverables.map((deliverable: any) => (
                  <Card key={deliverable.id} className="border-card-border">
                    <CardContent className="p-4 space-y-2">
                      <div className="flex items-start justify-between gap-2">
                        <div className="flex-1">
                          <p className="font-semibold text-sm line-clamp-1">{deliverable.title}</p>
                          <p className="text-xs text-muted-foreground">
                            Month {deliverable.monthNumber} - Video #{deliverable.videoNumber}
                          </p>
                        </div>
                        <Badge variant="outline" className="shrink-0">Approved</Badge>
                      </div>
                      {deliverable.reviewedAt && (
                        <p className="text-xs text-muted-foreground">
                          {format(new Date(deliverable.reviewedAt), "MMM d, yyyy")}
                        </p>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </div>
            </div>
          )}

          {revisionDeliverables.length > 0 && (
            <div className="space-y-4">
              <h3 className="font-semibold">Revision Requested ({revisionDeliverables.length})</h3>
              <div className="grid sm:grid-cols-2 gap-4">
                {revisionDeliverables.map((deliverable: any) => (
                  <Card key={deliverable.id} className="border-card-border">
                    <CardContent className="p-4 space-y-2">
                      <div className="flex items-start justify-between gap-2">
                        <div className="flex-1">
                          <p className="font-semibold text-sm line-clamp-1">{deliverable.title}</p>
                          <p className="text-xs text-muted-foreground">
                            Month {deliverable.monthNumber} - Video #{deliverable.videoNumber}
                          </p>
                        </div>
                        <Badge variant="secondary" className="shrink-0">Revision</Badge>
                      </div>
                      {deliverable.reviewNotes && (
                        <p className="text-xs text-muted-foreground">{deliverable.reviewNotes}</p>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </div>
            </div>
          )}

          {!deliverables || deliverables.length === 0 && (
            <Card className="border-card-border">
              <CardContent className="p-12 text-center">
                <Video className="h-12 w-12 text-muted-foreground/50 mx-auto mb-4" />
                <p className="text-muted-foreground">
                  No deliverables submitted yet
                </p>
              </CardContent>
            </Card>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/company-videos.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Play, Trash2, ExternalLink } from "lucide-react";
import { useState } from "react";

export default function CompanyVideos() {
  const { toast } = useToast();
  const { user } = useAuth();
  const [selectedVideo, setSelectedVideo] = useState<any>(null);

  // Fetch all offers for the company
  const { data: offers, isLoading } = useQuery<any[]>({
    queryKey: ["/api/company/offers"],
  });

  // Delete video mutation
  const deleteMutation = useMutation({
    mutationFn: async (videoId: string) => {
      await apiRequest("DELETE", `/api/offer-videos/${videoId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/company/offers"] });
      toast({
        title: "Video Deleted",
        description: "The promotional video has been removed successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: "Failed to delete video. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Collect all videos from all offers
  const allVideos = offers?.flatMap((offer: any) => 
    (offer.videos || []).map((video: any) => ({
      ...video,
      offerTitle: offer.title,
      offerId: offer.id,
    }))
  ) || [];

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div>
          <h1 className="text-3xl font-bold">Promotional Videos</h1>
          <p className="text-muted-foreground">Loading your videos...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold" data-testid="heading-company-videos">Promotional Videos</h1>
        <p className="text-muted-foreground">
          All promotional videos across your offers ({allVideos.length} total)
        </p>
      </div>

      {allVideos.length === 0 ? (
        <Card className="border-card-border">
          <CardContent className="p-12 text-center">
            <Play className="h-12 w-12 text-muted-foreground/50 mx-auto mb-4" />
            <p className="text-muted-foreground">No promotional videos uploaded yet</p>
            <p className="text-sm text-muted-foreground mt-2">
              Upload videos to your offers to showcase your products
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {allVideos.map((video: any) => (
            <Card
              key={video.id}
              className="hover-elevate border-card-border overflow-hidden"
              data-testid={`video-card-${video.id}`}
            >
              <div
                className="aspect-video relative bg-muted cursor-pointer"
                onClick={() => setSelectedVideo(video)}
              >
                {video.thumbnailUrl ? (
                  <img
                    src={video.thumbnailUrl}
                    alt={video.title}
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <div className="w-full h-full flex items-center justify-center">
                    <Play className="h-8 w-8 text-muted-foreground/50" />
                  </div>
                )}
                <div className="absolute inset-0 bg-black/20 flex items-center justify-center">
                  <Play className="h-12 w-12 text-white" />
                </div>
              </div>
              <CardContent className="p-4 space-y-3">
                <div>
                  <h4 className="font-semibold line-clamp-1" data-testid={`text-video-title-${video.id}`}>
                    {video.title}
                  </h4>
                  {video.description && (
                    <p className="text-sm text-muted-foreground line-clamp-2 mt-1">
                      {video.description}
                    </p>
                  )}
                </div>

                <div className="flex items-center gap-2">
                  <Badge variant="secondary" className="text-xs">
                    {video.offerTitle}
                  </Badge>
                </div>

                {video.creatorCredit && (
                  <p className="text-xs text-muted-foreground">
                    by {video.creatorCredit}
                  </p>
                )}

                <div className="flex gap-2 pt-2">
                  <Button
                    variant="outline"
                    size="sm"
                    className="flex-1"
                    onClick={(e) => {
                      e.stopPropagation();
                      window.open(`/company/offers/${video.offerId}`, '_blank');
                    }}
                    data-testid={`button-view-offer-${video.id}`}
                  >
                    <ExternalLink className="h-3 w-3 mr-1" />
                    View Offer
                  </Button>

                  <AlertDialog>
                    <AlertDialogTrigger asChild>
                      <Button
                        variant="destructive"
                        size="sm"
                        onClick={(e) => e.stopPropagation()}
                        data-testid={`button-delete-video-${video.id}`}
                      >
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </AlertDialogTrigger>
                    <AlertDialogContent>
                      <AlertDialogHeader>
                        <AlertDialogTitle>Delete Video?</AlertDialogTitle>
                        <AlertDialogDescription>
                          Are you sure you want to delete "{video.title}"? This action cannot be undone.
                        </AlertDialogDescription>
                      </AlertDialogHeader>
                      <AlertDialogFooter>
                        <AlertDialogCancel>Cancel</AlertDialogCancel>
                        <AlertDialogAction
                          onClick={() => deleteMutation.mutate(video.id)}
                          className="bg-destructive hover:bg-destructive/90"
                        >
                          Delete
                        </AlertDialogAction>
                      </AlertDialogFooter>
                    </AlertDialogContent>
                  </AlertDialog>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Video Preview Dialog */}
      {selectedVideo && (
        <AlertDialog open={!!selectedVideo} onOpenChange={() => setSelectedVideo(null)}>
          <AlertDialogContent className="max-w-4xl">
            <AlertDialogHeader>
              <AlertDialogTitle>{selectedVideo.title}</AlertDialogTitle>
              {selectedVideo.description && (
                <AlertDialogDescription>{selectedVideo.description}</AlertDialogDescription>
              )}
            </AlertDialogHeader>
            <div className="aspect-video bg-muted rounded-md overflow-hidden">
              <video
                src={selectedVideo.videoUrl}
                controls
                className="w-full h-full"
                autoPlay
              >
                Your browser does not support the video tag.
              </video>
            </div>
            {(selectedVideo.creatorCredit || selectedVideo.originalPlatform) && (
              <div className="flex gap-4 text-sm text-muted-foreground">
                {selectedVideo.creatorCredit && (
                  <span>Creator: {selectedVideo.creatorCredit}</span>
                )}
                {selectedVideo.originalPlatform && (
                  <span>Platform: {selectedVideo.originalPlatform}</span>
                )}
              </div>
            )}
            <AlertDialogFooter>
              <AlertDialogCancel>Close</AlertDialogCancel>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>
      )}
    </div>
  );
}

client/src/pages/creator-retainers.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  FormDescription,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { DollarSign, Video, Calendar, Briefcase, Send } from "lucide-react";
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Link } from "wouter";

const applyRetainerSchema = z.object({
  message: z.string().min(20, "Message must be at least 20 characters"),
  portfolioLinks: z.string().optional(),
  proposedStartDate: z.string().optional(),
});

type ApplyRetainerForm = z.infer<typeof applyRetainerSchema>;

export default function CreatorRetainers() {
  const { toast } = useToast();
  const [selectedContract, setSelectedContract] = useState<any>(null);
  const [open, setOpen] = useState(false);

  const { data: contracts, isLoading } = useQuery<any[]>({
    queryKey: ["/api/retainer-contracts"],
  });

  const { data: myApplications } = useQuery<any[]>({
    queryKey: ["/api/creator/retainer-applications"],
  });

  const form = useForm<ApplyRetainerForm>({
    resolver: zodResolver(applyRetainerSchema),
    defaultValues: {
      message: "",
      portfolioLinks: "",
      proposedStartDate: "",
    },
  });

  const applyMutation = useMutation({
    mutationFn: async (data: ApplyRetainerForm) => {
      const payload = {
        message: data.message,
        portfolioLinks: data.portfolioLinks
          ? data.portfolioLinks.split(",").map((link) => link.trim()).filter(Boolean)
          : [],
        proposedStartDate: data.proposedStartDate || undefined,
      };
      return await apiRequest("POST", `/api/creator/retainer-contracts/${selectedContract.id}/apply`, payload);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/creator/retainer-applications"] });
      toast({
        title: "Application Submitted",
        description: "Your retainer application has been sent to the company.",
      });
      setOpen(false);
      setSelectedContract(null);
      form.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to submit application",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ApplyRetainerForm) => {
    applyMutation.mutate(data);
  };

  const hasApplied = (contractId: string) => {
    return myApplications?.some((app: any) => app.contractId === contractId);
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div>
          <h1 className="text-3xl font-bold">Monthly Retainers</h1>
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold" data-testid="heading-creator-retainers">
          Monthly Retainers
        </h1>
        <p className="text-muted-foreground">
          Browse ongoing monthly video production contracts
        </p>
      </div>

      {contracts && contracts.length === 0 ? (
        <Card className="border-card-border">
          <CardContent className="p-12 text-center">
            <Briefcase className="h-12 w-12 text-muted-foreground/50 mx-auto mb-4" />
            <h3 className="font-semibold mb-2">No Retainer Contracts Available</h3>
            <p className="text-muted-foreground">
              Check back later for new monthly retainer opportunities
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-6">
          {contracts?.map((contract: any) => {
            const applied = hasApplied(contract.id);
            return (
              <Card
                key={contract.id}
                className="hover-elevate border-card-border"
                data-testid={`retainer-card-${contract.id}`}
              >
                <CardHeader>
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <CardTitle className="text-xl" data-testid={`text-retainer-title-${contract.id}`}>
                          {contract.title}
                        </CardTitle>
                        {applied && (
                          <Badge variant="secondary">Applied</Badge>
                        )}
                      </div>
                      <p className="text-sm text-muted-foreground mb-2">
                        by {contract.company?.tradeName || contract.company?.legalName || "Company"}
                      </p>
                      <p className="text-muted-foreground line-clamp-3">
                        {contract.description}
                      </p>
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid sm:grid-cols-2 lg:grid-cols-4 gap-4">
                    <div className="flex items-center gap-3">
                      <div className="h-10 w-10 rounded-md bg-primary/10 flex items-center justify-center shrink-0">
                        <DollarSign className="h-5 w-5 text-primary" />
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Monthly Payment</p>
                        <p className="font-semibold">
                          ${parseFloat(contract.monthlyAmount).toLocaleString()}
                        </p>
                      </div>
                    </div>

                    <div className="flex items-center gap-3">
                      <div className="h-10 w-10 rounded-md bg-primary/10 flex items-center justify-center shrink-0">
                        <Video className="h-5 w-5 text-primary" />
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Videos/Month</p>
                        <p className="font-semibold">{contract.videosPerMonth}</p>
                      </div>
                    </div>

                    <div className="flex items-center gap-3">
                      <div className="h-10 w-10 rounded-md bg-primary/10 flex items-center justify-center shrink-0">
                        <Calendar className="h-5 w-5 text-primary" />
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Duration</p>
                        <p className="font-semibold">{contract.durationMonths} months</p>
                      </div>
                    </div>

                    <div className="flex items-center gap-3">
                      <div className="h-10 w-10 rounded-md bg-primary/10 flex items-center justify-center shrink-0">
                        <Briefcase className="h-5 w-5 text-primary" />
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Platform</p>
                        <p className="font-semibold">{contract.requiredPlatform}</p>
                      </div>
                    </div>
                  </div>

                  {contract.contentGuidelines && (
                    <div className="pt-4 border-t">
                      <h4 className="font-semibold text-sm mb-2">Content Guidelines</h4>
                      <p className="text-sm text-muted-foreground line-clamp-2">
                        {contract.contentGuidelines}
                      </p>
                    </div>
                  )}

                  {contract.niches && contract.niches.length > 0 && (
                    <div className="flex gap-2 flex-wrap">
                      {contract.niches.map((niche: string, index: number) => (
                        <Badge key={index} variant="outline">
                          {niche}
                        </Badge>
                      ))}
                    </div>
                  )}

                  <div className="flex gap-2 pt-2">
                    <Link href={`/retainers/${contract.id}`} className="flex-1">
                      <Button
                        variant="outline"
                        className="w-full"
                        data-testid={`button-view-details-${contract.id}`}
                      >
                        View Details
                      </Button>
                    </Link>
                    {!applied && (
                      <Button
                        onClick={() => {
                          setSelectedContract(contract);
                          setOpen(true);
                        }}
                        className="flex-1"
                        data-testid={`button-apply-${contract.id}`}
                      >
                        <Send className="h-4 w-4 mr-2" />
                        Apply Now
                      </Button>
                    )}
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      )}

      <Dialog open={open} onOpenChange={setOpen}>
        <DialogContent className="max-w-lg">
          <DialogHeader>
            <DialogTitle>Apply for Retainer Contract</DialogTitle>
            <DialogDescription>
              Submit your application for: {selectedContract?.title}
            </DialogDescription>
          </DialogHeader>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="message"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Cover Message</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Introduce yourself and explain why you're a great fit for this retainer..."
                        rows={6}
                        data-testid="input-application-message"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="portfolioLinks"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Portfolio Links (Optional)</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="https://tiktok.com/@yourprofile, https://instagram.com/yourprofile"
                        data-testid="input-portfolio-links"
                        {...field}
                      />
                    </FormControl>
                    <FormDescription className="text-xs">
                      Comma-separated URLs to your social profiles
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="proposedStartDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Proposed Start Date (Optional)</FormLabel>
                    <FormControl>
                      <Input
                        type="date"
                        data-testid="input-start-date"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <DialogFooter>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => {
                    setOpen(false);
                    setSelectedContract(null);
                  }}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={applyMutation.isPending}
                  data-testid="button-submit-application"
                >
                  {applyMutation.isPending ? "Submitting..." : "Submit Application"}
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

